import React, { useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { FaBars, FaTimes, FaHome, FaUser, FaBookmark, FaCog, FaSignOutAlt, FaBell } from 'react-icons/fa';
import logo from '../assets/logo.png';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../features/auth/userSlice';
import SearchBar from '../components/SearchBar';

const Layout = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const user = useSelector((state) => state.user.user);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleLogout = () => {
    dispatch(logout());
    navigate('/login');
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside
        className={`bg-white text-gray-800 w-64 transform ${
          isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
        } md:w-64 fixed inset-y-0 left-0 transition-transform duration-300 ease-in-out z-50 shadow-lg`}
      >
        {/* Sidebar header with logo and profile */}
        <div className="p-6 border-b flex items-center justify-between">
          {/* Logo Section */}
          <div className="flex items-center">
            <img src={logo} alt="Logo" className="w-10 h-10" />
            <h1 className="text-2xl font-bold ml-2">Connectify</h1>
          </div>

          {/* Close button (visible on all screens except very large ones) */}
          <button
            className="text-gray-800"
            onClick={toggleSidebar}
          >
            <FaTimes size={20} />
          </button>
        </div>

        {/* Profile Section */}
        <div className="flex flex-col border-b items-center p-6">
          <img
            src={user?.profile_pic || 'https://via.placeholder.com/100'}
            alt="Profile"
            className="w-16 h-16 rounded-full object-cover"
          />
          <h2 className="mt-4 text-lg font-semibold">{user?.username || 'John Doe'}</h2>
        </div>

        {/* Sidebar Links */}
        <nav className="mt-4 space-y-2">
          <a
            href="/home"
            className="flex items-center p-3 hover:bg-gray-200 rounded-md"
            onClick={() => setIsSidebarOpen(false)}
          >
            <FaHome className="mr-3" /> Home
          </a>
          <a
            href="/profile"
            className="flex items-center p-3 hover:bg-gray-200 rounded-md"
            onClick={() => setIsSidebarOpen(false)}
          >
            <FaUser className="mr-3" /> Profile
          </a>
          <a
            href="/bookmarks"
            className="flex items-center p-3 hover:bg-gray-200 rounded-md"
            onClick={() => setIsSidebarOpen(false)}
          >
            <FaBookmark className="mr-3" /> Bookmarks
          </a>
          <a
            href="/settings"
            className="flex items-center p-3 hover:bg-gray-200 rounded-md"
            onClick={() => setIsSidebarOpen(false)}
          >
            <FaCog className="mr-3" /> Settings
          </a>
          <button
            onClick={() => {
              handleLogout();
              setIsSidebarOpen(false);
            }}
            className="flex items-center p-3 hover:bg-gray-200 w-full rounded-md"
          >
            <FaSignOutAlt className="mr-3" /> Logout
          </button>
        </nav>
      </aside>

      {/* Main Content */}
      <div className="flex flex-col flex-grow">
        {/* Top Navigation Bar */}
        <header className="bg-white shadow-md fixed inset-x-0 top-0 z-30 flex items-center justify-between px-6 py-4">
          {/* Left Side - Hamburger Menu */}
          <div className="flex items-center">
            <button onClick={toggleSidebar} className="text-gray-800 lg:hidden mr-4">
              <FaBars />
            </button>
          </div>

          {/* Search Bar */}
          <div className="w-full max-w-md">
            <SearchBar />
          </div>

          {/* Right Side - Notifications and User Profile */}
          <div className="flex items-center space-x-6">
            <button className="text-gray-800">
              <FaBell size={24} />
            </button>
            <img
              src={user?.profile_pic || 'https://via.placeholder.com/30'}
              alt="Profile"
              className="w-8 h-8 rounded-full object-cover"
            />
          </div>
        </header>

        {/* Main Feed Area */}
        <main className="mt-16 flex-grow p-6 bg-gray-100 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
