    if (openCommentId === postId) {
      setOpenCommentId(null); // Close the comments section if it's already open
    } else {
      setOpenCommentId(postId); // Open the comments section for the specific post
    }
  };


  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
  {/* Cover Photo */}
  <div
    className="relative h-48 sm:h-60 bg-cover bg-center cursor-pointer"
    style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x500'})` }}
  >
    <label
      htmlFor="cover-upload"
      className="absolute top-2 right-2 sm:top-4 sm:right-4 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
    >
      <FaCamera className="text-gray-600 text-lg" />
    </label>
    <input
      id="cover-upload"
      type="file"
      accept="image/jpeg,image/jpg,image/png"
      onChange={(e) => handleFileChange(e, 'cover')}
      className="hidden"
    />
    <button
      onClick={() => openModal('cover')}
      className="absolute top-2 right-12 sm:top-4 sm:right-12 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
    >
      Upload
    </button>

    {/* Profile Picture */}
    <div className="absolute bottom-0 left-2 sm:left-4 transform -translate-y-1/2">
      <div className="relative">
        <img
          src={profile.profile_pic || 'https://via.placeholder.com/100'}
          alt="Profile"
          accept="image/jpeg,image/jpg,image/png"
          className="w-20 h-20 sm:w-24 sm:h-24 rounded-full border-4 border-white shadow-md cursor-pointer object-cover"
          onClick={() => openModal('profile')}
        />
        <label
          htmlFor="profile-upload"
          className="absolute bottom-1 left-10 sm:left-12 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
        >
          <FaCamera className="text-gray-600 text-lg" />
        </label>
        <input
          id="profile-upload"
          type="file"
          onChange={(e) => handleFileChange(e, 'profile')}
          className="hidden"
        />
        <button
          onClick={() => openModal('profile')}
          className="absolute bottom-1 left-16 sm:left-24 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
        >
          Upload
        </button>
      </div>
    </div>
  </div>

  {/* Profile Details */}
  <div className="p-4 sm:p-6">
    {/* Profile Section */}
    <div className="flex flex-col sm:flex-row justify-between items-center mb-4">
      {/* User Info */}
      <div className="text-center sm:text-left">
        <h2 className="text-xl sm:text-2xl font-semibold">{profile.first_name}</h2>
        <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{`@${profile.username}`}</h2>
      </div>

      {/* Action Buttons */}
      <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-4 mt-4 sm:mt-0">
        {/* Profile Link */}
        <Link
          to="/profile-detail"
          className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white w-full sm:w-auto"
        >
          Profile
        </Link>
        {/* Edit Profile Link */}
        <Link
          to="/edit-profile"
          className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white w-full sm:w-auto"
        >
          Edit Profile
        </Link>
      </div>
    </div>

    {/* Bio Section */}
   <p className="text-gray-600 mb-6 text-center sm:text-left">
  {profile.bio || 'No bio available'}
</p>

    <PostBox onPostSubmit={handleNewPost} />

    {/* Posts List */}
    {/* <div>
      {posts.map((post) => (
        <div key={post.id} className="p-4 border-b border-gray-200">
          <p>{post.content}</p>
          {post.image && <img src={post.image} alt="Post" className="mt-2 max-w-full h-auto" />}
          {post.video && <video src={post.video} controls className="mt-2 max-w-full h-auto" />}
        </div>
      ))}
    </div> */}
 <div className="flex justify-center">
  <div className="w-full md:max-w-2xl lg:max-w-4xl mt-6">
    {posts.map((post) => (
      <div key={post.id} className="bg-white border border-gray-300 rounded-lg shadow-md mb-6">
        {/* Post Header: Profile picture and Username */}
        <div className="flex items-center p-4">
          <img
            src={user.profile_pic || 'https://via.placeholder.com/40'}
            alt="Profile"
            className="w-10 h-10 rounded-full object-cover"
          />
          <div className="ml-3">
            <p className="font-semibold text-gray-800">{user.username}</p>
            <p className="text-sm text-gray-500">Posted {new Date(post.created_at).toLocaleDateString()}</p>
          </div>
        </div>

        {/* Post Content */}
        <div className="px-4">
          {post.content && <p className="text-gray-800 mb-3">{post.content}</p>}
          {post.image && (
            <img
              src={post.image}
              alt="Post"
              className="rounded-lg mt-2 w-full h-auto object-cover"
            />
          )}
          {post.video && (
            <video
              src={post.video}
              controls
              className="rounded-lg mt-2 w-full h-auto"
            />
          )}
        </div>

        {/* Post Footer: Like, Comment, Bookmark */}
        <div className="flex justify-between items-center px-4 py-2 border-t border-gray-200">
          <div className="flex items-center space-x-4">
            {/* Like Button */}
            <button 
              onClick={() => handleLike(post.id)} 
              className={`flex items-center ${post.is_liked ? 'text-red-500' : 'text-gray-600'} hover:text-red-500`}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                strokeWidth="2"
                className="w-6 h-6 mr-1"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"
                />
              </svg>
              Like {post.total_likes > 0 && <span>{post.total_likes}</span>}
            </button>

            {/* Comment Button */}
            <button 
              onClick={() => toggleComments(post.id)} 
              className="flex items-center text-gray-600 hover:text-blue-500"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                strokeWidth="2"
                className="w-6 h-6 mr-1"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M7 8h10M7 12h7m-7 4h10m0 6v-6h4v8.59a1.5 1.5 0 01-2.29 1.25L18 21H6a1 1 0 01-1-1V4a1 1 0 011-1h12v2H6v16h10z"
                />
              </svg>
              Comment {post.total_comments > 0 && <span>{post.total_comments}</span>}
            </button>
          </div>

          {/* Bookmark Button */}
          <button className="text-gray-600 hover:text-blue-500">
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path d="M5 5v15l7-5 7 5V5z" strokeLinecap="round" strokeLinejoin="round" />
            </svg>
          </button>
        </div>

        {/* Comments Section */}
        {post.showComments && (
          <div className="px-4 pb-4">
            <div className="bg-gray-100 p-4 rounded-lg mt-4">
              {post.comments.map((comment) => (
                <div key={comment.id} className="mb-4">
                  <div className="flex items-center">
                    <img
                      src={comment.user.profile_pic || 'https://via.placeholder.com/30'}
                      alt="Comment Profile"
                      className="w-8 h-8 rounded-full object-cover"
                    />
                    <div className="ml-3">
                      <p className="font-semibold text-gray-800">{comment.user.username}</p>
                      <p className="text-sm text-gray-500">{comment.content}</p>
                    </div>
                  </div>
                </div>
              ))}
              {/* Add Comment Form */}
              <div className="mt-4">
                <textarea
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="Write a comment..."
                  className="w-full p-2 border border-gray-300 rounded-lg"
                />
                <button
                  onClick={() => handleAddComment(post.id)}
                  className="mt-2 px-4 py-2 bg-blue-500 text-white rounded-lg"
                >
                  Post Comment
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    ))}
  </div>
</div>




  </div>

  {/* Cropper Modal */}
  <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="w-full max-w-2xl mx-auto mt-16">
    <ImageCropper modalImage={modalImage} onSave={handleImageUpload} onClose={closeModal} ref={cropperRef} />
  </Modal>
</div>

  );
};

export default ProfilePage;
