import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';
import { updateUserProfileImage } from '../features/auth/userSlice';
import { showErrorToast } from '../components/ErroToast';
import ImageCropper from '../components/Cropper';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import PostBox from '../components/PostBoxComponent';
import { showSuccessToast } from '../components/CustomToast';
import CommentItem from '../components/CommentItem';
import Picker from '@emoji-mart/react';
import data from '@emoji-mart/data';
import { FaSmile, FaTimes } from 'react-icons/fa';
import { FaUser, FaEdit } from 'react-icons/fa';
import ProfileStats from '../components/ProfileStats';

Modal.setAppElement('#root');

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([
    { id: 1, content: 'Just started a new project! Excited to share the progress.' },
    { id: 2, content: 'Attended a fantastic workshop today. Learned so much!' },
    { id: 3, content: 'Feeling motivated to code more today!' },
  ]);
  const [newPost, setNewPost] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [openCommentId, setOpenCommentId] = useState(null); 
  const [imageType, setImageType] = useState(''); // 'cover' or 'profile'
  const cropperRef = useRef(null);
  const [likedPosts, setLikedPosts] = useState({});   
  const [comments, setComments] = useState({}); 
  const [newComment, setNewComment] = useState(""); 
  const [replyingToCommentId, setReplyingToCommentId] = useState(null); 
  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);
  const [openRepliesId, setOpenRepliesId] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [commentText, setCommentText] = useState('');
  const [followerCount, setFollowerCount] = useState(0);
  const [followingCount, setFollowingCount] = useState(0);
  const [bookmarks, setBookmarks] = useState([]);
  const [bookmarkPosts, setbookmarkPosts] = useState({});  
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch()
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
       
        setProfile(response.data);
        setFollowerCount(response.data.followers);
        setFollowingCount(response.data.following);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    if (user && token) {
      fetchProfile();
    }
  }, [user, token]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/api/posts/?user_id=${user.id}`, {
     headers: {
         Authorization: `Bearer ${token}`,
     },
   });
     setPosts(response.data); 
     console.log(response.data)// Set the fetched posts
   } catch (err) {
     console.log("Error fetching posts");
   }
  };
  const fetchBookmarks = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/api/bookmarks`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
      });  // Fetch user's bookmarks
      setBookmarks(response.data.map(bookmark => bookmark.post));
    } catch (error) {
      console.error('Error fetching bookmarks:', error);
    }
  };
  const fetchComments = async (postId) => {
    try {
      const response = await axios.get(`${BASE_URL}/api/posts/${postId}/comments/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setComments((prevComments) => ({
        ...prevComments,
        [postId]: response.data,
        
      }));
    } catch (error) {
      console.log("Error fetching comments");
    }
  };
  
  
  useEffect(() => {
    if (user && token) {
      fetchPosts();
      fetchBookmarks();
    }
  }, [user, token]);
  useEffect(() => {
    if (posts.length > 0) {
      posts.forEach((post) => {
        console.log(`Correct post id: ${post.id}`);
        fetchComments(post.id);
      });
    }
  }, [posts]);

  useEffect(() => {
    const fetchLikeStatus = async () => {
      try {
        const likeStatuses = {};
        for (let post of posts) {
          const response = await fetch(`${BASE_URL}/api/posts/${post.id}/check_liked/`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${token}`,
            },
          });

          if (response.ok) {
            const result = await response.json();
            likeStatuses[post.id] = result.liked; // Assume the response contains { liked: true/false }
            console.log('check liked');
            console.log(result);
          }
        }
        setLikedPosts(likeStatuses);
      } catch (error) {
        console.error('Error fetching like status:', error);
      }
    };

    const fetchBookmarkStatus = async () => {
      try {
        const bookmarkPosts = {};
        for (let post of posts) {
          const response = await fetch(`${BASE_URL}/api/posts/${post.id}/check_bookmarked/`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${token}`,
            },
          });

          if (response.ok) {
            const result = await response.json();
            bookmarkPosts[post.id] = result.bookmarked; // Assume the response contains { liked: true/false }
           
          }
        }
        setbookmarkPosts(bookmarkPosts);
      } catch (error) {
        console.error('Error fetching like status:', error);
      }
    };

    fetchLikeStatus();
    fetchBookmarkStatus();
  }, [posts, token]);


  const handleFileChange = (event, type) => {
    const fileInput = event.target;
    const file = event.target.files[0];
    if (file) {
      const validImageTypes = ['image/jpeg', 'image/jpg', 'image/png'];
      if (!validImageTypes.includes(file.type)) {
        // setError('Please select a valid image file (JPEG, JPG, or PNG).');
        showErrorToast('Please select a valid image file (JPEG, JPG, or PNG).');
        fileInput.value = null; 
        return;
      }
    }
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setModalImage(reader.result);
        setImageType(type);
        setModalIsOpen(true);
        fileInput.value = null; 
      };
      reader.readAsDataURL(file);
    } else {
      console.error('No file selected.');
    }
  };

  const handleImageUpload = async () => {
    if (!cropperRef.current) return;

    const cropper = cropperRef.current.cropper;
    const canvas = cropper.getCroppedCanvas();
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append(imageType === 'cover' ? 'cover_pic' : 'profile_pic', blob, 'cropped-image.jpg');

      try {
        await axios.patch(`${BASE_URL}/api/users/profile/update/`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        });

        const updatedImageURL = URL.createObjectURL(blob);
        setProfile((prevProfile) => ({
          ...prevProfile,
          [imageType === 'cover' ? 'cover_pic' : 'profile_pic']: updatedImageURL,
        }));

        // Dispatch the action to update the Redux store
        if (imageType === 'profile') {
          dispatch(updateUserProfileImage(updatedImageURL));
        }

        closeModal();

      } catch (err) {
        setError('An error occurred while uploading the image.');
      }
    }, 'image/jpeg');
  };

  const handlePostSubmit = async () => {
    if (!newPost.trim()) {
      setError('Post content cannot be empty.');
      return;
    }

    try {
      setPosts((prevPosts) => [
        ...prevPosts,
        { id: Date.now(), content: newPost },
      ]);
      setNewPost('');
    } catch (err) {
      setError('An error occurred while submitting the post.');
    }
  };

  const openModal = (type) => {
    setImageType(type);
    document.getElementById(`${type}-upload`).click(); // Trigger the file input click
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalImage('');
  };

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
      </div>
    );
  }
  

  const handleNewPost = async (postData) => {
    const userId = user.id;
    setLoading(true);  
    if (!postData.content || postData.content.trim() === '') {
      showErrorToast("Content cannot be empty.");
      setLoading(false); 
      return;
    }
    try {
      const formData = new FormData();
      formData.append('content', postData.content);
      formData.append('user', user.id);

      if (postData.image) {
        const imageBlob = await fetch(postData.image).then((res) => res.blob());
        formData.append('image', imageBlob, 'post.jpg');
      }
  
      console.log(`video:${postData.video}`)
      if (postData.video) {
        const videoBlob = await fetch(postData.video).then((res) => res.blob());
        const videoSizeMB = videoBlob.size / (1024 * 1024);
        if (videoSizeMB > 100) {
          showErrorToast("Video size exceeds 100MB. Please upload a smaller file.");
          setLoading(false); 
          return; 
        }
        formData.append('video', videoBlob, 'post.mp4');
      }
      formData.append('is_approved', true);
      for (const [key, value] of formData.entries()) {
        console.log(`${key}:`, value);
      }
      const response = await axios.post(`${BASE_URL}/api/posts/`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      
      setPosts((prevPosts) => [response.data, ...prevPosts]);
      showSuccessToast("Post successfully submitted");
    } catch (err) {
       showErrorToast("Error while submitting!")
    }finally {
      setLoading(false);  
    }
  };
  const handleLike = async  (postId) => {
    try {
      const response = await fetch(`${BASE_URL}/api/likes/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ post: postId }),
      });

      if (response.status === 201) {
        console.log('Post liked');
        setLikedPosts((prev) => ({ ...prev, [postId]: true }));
      } else if (response.status === 204) {
        console.log('Post unliked');
        setLikedPosts((prev) => ({ ...prev, [postId]: false }));
      } else {
        console.log('Error:', await response.json());
      }
      fetchPosts();
    } catch (error) {
      console.error('Error liking/unliking post:', error);
    }
  };



  const handleSubmitComment = async (postId, commentText, parentId = null) => {
    if (!commentText.trim()) {
      showErrorToast('Empty Comment Box');
      return;
    }
    console.log(postId,commentText,user.id,parentId,token);
    try {
      const response = await axios.post(`${BASE_URL}/api/comments/`, {
        post: postId,
        content: commentText,
        user: user.id,
        parent: parentId, // Include parentId here
      }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
  
      const newCommentData = response.data;
      setComments((prevComments) => ({
        ...prevComments,
        [postId]: [...(prevComments[postId] || []), newCommentData],
      }));
  
      setNewComment("");
      setReplyingToCommentId(null);
     
      showSuccessToast("Comment submitted successfully");
      fetchPosts();
    } catch (error) {
      showErrorToast("Error submitting comment");
    }
  };
  const handleToggleComments = (postId) => {
    if (openCommentId === postId) {
      setOpenCommentId(null); // Close the comments section if it's already open
    } else {
      setOpenCommentId(postId); // Open the comments section for the specific post
    }
  };

  const handleAddEmoji = (emoji) => {
    setCommentText((prev) => prev + emoji.native); // Append the selected emoji to the comment text
  };
 

  const handleBookmark = async (postId) => {
    try {
      
      if (bookmarkPosts[postId]) {
        // If already bookmarked, unbookmark the post
        await axios.delete(`${BASE_URL}/api/bookmarks/${postId}`,{
          headers: {
              Authorization: `Bearer ${token}`,
          },
        });
        setbookmarkPosts((prevState) => ({
          ...prevState,
          [postId]: false,
        }));
        showErrorToast('Post removed from Bookmarks');
      } else {
        // If not bookmarked, bookmark the post
        await axios.post(`${BASE_URL}/api/bookmarks/`, { post: postId }, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setbookmarkPosts((prevState) => ({
          ...prevState,
          [postId]: true,
        }));
        showSuccessToast('Added to Bookmarks');
      }
    } catch (error) {
      console.error('Error updating bookmark:', error);
    }
  };

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      
  {/* Profile Header */}
  
  <ProfileHeader profile={profile} handleFileChange={handleFileChange} openModal={openModal}/>
  {/* Profile Details */}
  <div className="p-4 sm:p-6">
    {/* Profile Section */}
    <div className="flex flex-col sm:flex-row justify-between items-center mb-4">
  {/* User Info */}
  <div className="text-center sm:text-left flex items-center space-x-4">
    <div>
      <h2 className="text-xl sm:text-2xl font-semibold">{profile.first_name}</h2>
      <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{`@${profile.username}`}</h2>
    </div>
    
    {/* Profile and Edit Icons */}
    <div className="flex space-x-2 ml-4">
      {/* Profile Icon Link */}
      <Link
        to="/profile-detail"
        className="text-blue-500 hover:text-blue-700"
        title="Profile"
      >
        <FaUser className="text-xl" />
      </Link>

      {/* Edit Profile Icon Link */}
      <Link
        to="/edit-profile"
        className="text-blue-500 hover:text-blue-700"
        title="Edit Profile"
      >
        <FaEdit className="text-xl" />
      </Link>
    </div>

  <ProfileStats userId={user.id} followerCount={followerCount} followingCount={followingCount}/>
  </div>
</div>

    {/* Bio Section */}
   <p className="text-gray-600 mb-6 text-center sm:text-left">
  {profile.bio || 'No bio available'}
</p>
{loading && (
      <div className="flex justify-center items-center mb-2">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
      </div>
    )}

    <PostBox onPostSubmit={handleNewPost} />

  
   <div className="flex justify-center">
      <div className="w-full md:max-w-2xl lg:max-w-4xl mt-6">
        {posts.map((post) => (
          <div key={post.id} className="bg-white border border-gray-300 rounded-lg shadow-md mb-6">
            {/* Post Header: Profile picture and Username */}
            <div className="flex items-center p-4">
              <img
                src={user.profile_pic || 'https://via.placeholder.com/40'}
                alt="Profile"
                className="w-10 h-10 rounded-full object-cover"
              />
              <div className="ml-3">
                <p className="font-semibold text-gray-800">{user.username}</p>
                <p className="text-sm text-gray-500">Posted {new Date(post.created_at).toLocaleDateString()}</p>
              </div>
           

            </div>

            {/* Post Content */}
            <div className="px-4">
              {post.content && <p className="text-gray-800 mb-3">{post.content}</p>}
              {post.image && (
                <img
                  src={post.image}
                  alt="Post"
                  className="rounded-lg mt-2 w-full h-auto object-cover"
                />
              )}
              {post.video && (
                <video
                  src={post.video}
                  controls
                  className="rounded-lg mt-2 w-full h-auto"
                />
              )}
            </div>

            {/* Post Footer: Like, Comment, Bookmark */}
            <div className="flex justify-between items-center px-4 py-2 border-t border-gray-200">
              <div className="flex items-center space-x-4">
                {/* Like Button */}
                <button
                  className={`flex items-center ${likedPosts[post.id] ? 'text-red-500' : 'text-gray-600'} hover:text-red-500`}
                  onClick={() => handleLike(post.id)} // Like/unlike post on click
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill={likedPosts[post.id] ? 'red' : 'none'} // Fill heart if liked
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                    strokeWidth="2"
                    className="w-6 h-6 mr-1"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"
                    />
                  </svg>
                  {post.total_likes === 0 ? 'Like' : <span>{post.total_likes}</span>}
                </button>

                {/* Comment Button */}
                <button
                  className="flex items-center text-gray-600 hover:text-blue-500"
                  onClick={() => handleToggleComments(post.id)} // Toggle comments on click
                >
                     
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                    strokeWidth="2"
                    className="w-6 h-6 mr-1"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      d="M7 8h10M7 12h7m-7 4h10m0 6v-6h4v8.59a1.5 1.5 0 01-2.29 1.25L18 21H6a1 1 0 01-1-1V4a1 1 0 011-1h12v2H6v16h10z"
                    />
                  </svg>
                  {post.total_comments === 0 ? 'Comment' : <span>{post.total_comments}</span>}
                  
                </button>
              </div>

              {/* Bookmark Button */}
              <button
                className="text-gray-600 hover:text-blue-500"
                onClick={() => handleBookmark(post.id)} // Call the bookmark handler
              >
                <svg
                  className="w-6 h-6"
                  fill={bookmarkPosts[post.id] ? 'currentColor' : 'none'} // Conditional fill
                  stroke="currentColor"
                  strokeWidth="2"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path d="M5 5v15l7-5 7 5V5z" strokeLinecap="round" strokeLinejoin="round" />
                </svg>
              </button>
            </div>

            
        {/* Comment Section */}
        {openCommentId === post.id && (
                <div className="p-4 border-t border-gray-200">
                    {/* List Existing Comments */}
                    {comments[post.id] && comments[post.id].length > 0 ? (
                        comments[post.id].map((comment) => (
                            <CommentItem
                                key={comment.id}
                                comment={comment}
                                postId={post.id}
                                handleSubmitComment={handleSubmitComment}
                                replyingToCommentId={replyingToCommentId}
                                setReplyingToCommentId={setReplyingToCommentId}
                            />
                        ))
                    ) : (
                        <p className="text-gray-500">No comments yet. Be the first to comment!</p>
                    )}

                    {/* Comment Form for the post */}
                    <form
                        onSubmit={(e) => {
                            e.preventDefault();
                            if (commentText.trim()) {
                                handleSubmitComment(post.id, commentText); // No parentId means it's a top-level comment
                                setCommentText(''); // Clear the input field after submitting
                            }
                        }}
                        className="relative" // Added for positioning the emoji button
                    >
                        <input
                            type="text"
                            name="comment"
                            value={commentText}
                            onChange={(e) => setCommentText(e.target.value)} // Update commentText on input change
                            placeholder="Write a comment..."
                            className="w-full p-2 pr-10 border border-gray-300 rounded-lg focus:outline-none"
                        />
                        <button
                            type="button"
                            onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                            className="absolute right-2 top-2 text-gray-500 hover:text-gray-800"
                        >
                            {showEmojiPicker ? (
                                <FaTimes size={20} className="text-red-500" />
                            ) : (
                                <FaSmile size={20} className="text-gray-500" />
                            )}
                        </button>

                        {showEmojiPicker && (
                            <div className="absolute mt-12 right-0">
                                <Picker data={data} onEmojiSelect={handleAddEmoji} />
                            </div>
                        )}

                        <button
                            type="submit"
                            className="bg-blue-500 text-white mt-2 px-4 py-2 rounded-lg"
                        >
                            Post Comment
                        </button>
                    </form>
                </div>
            )}


          </div>
        ))}
      </div>
    </div>




  </div>

  {/* Cropper Modal */}
  <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="w-full max-w-2xl mx-auto mt-16">
    <ImageCropper modalImage={modalImage} onSave={handleImageUpload} onClose={closeModal} ref={cropperRef} />
  </Modal>
</div>

  );
};

export default ProfilePage;
