// src/layouts/Layout.jsx
import React, { useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { FaBars } from 'react-icons/fa';
import logo from '../assets/logo.png';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../features/auth/userSlice';
import SearchBar from '../components/SearchBar';
const Layout = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const user = useSelector((state) => state.user.user); 
  const dispatch=useDispatch();
  const navigate=useNavigate();
  console.log(user)
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleLogout=()=>{
    dispatch(logout());
    navigate('/login')
  }

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside
        className={`bg-gray-800 text-white ${
          isSidebarOpen ? 'block' : 'hidden'
        } fixed inset-y-0 left-0 transform md:translate-x-0 z-30 w-64 md:block transition-all duration-300 ease-in-out`}
      >
        {/* Hamburger Button for Mobile */}
        <div className="flex justify-end p-4 md:hidden">
          {/* Visible only on smaller screens */}
          <button onClick={toggleSidebar} className="text-white">
            <FaBars />
          </button>
        </div>

        {/* Profile Section */}
        <div className="flex items-center p-4">
  <img
    src={user?.profile_pic || 'https://via.placeholder.com/30'} // Dummy rounded profile image
    alt="Profile"
    className="w-10 h-10 rounded-full mr-2 object-cover" 
  />
  <span>{user?.username || 'John Doe'}</span>
</div>


        {/* Sidebar Links */}
        <nav className="mt-4 px-2 space-y-2">
          <a href="profile" className="block py-2 px-3 hover:bg-gray-700 rounded">
            Profile
          </a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
            Dashboard
          </a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
            Settings
          </a>
          <button
            onClick={handleLogout} // Attach the logout handler
            className="block py-2 px-3 w-full text-left hover:bg-gray-700 rounded"
          >
            Logout
          </button>
        </nav>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col ml-0 md:ml-64 transition-all duration-300 ease-in-out">
        {/* Header */}
        <header className="bg-white shadow-md flex items-center justify-between px-6 py-4 md:ml-0">

        <div className="flex items-center mb-1">
        <img src={logo} alt="Logo" className="w-10 h-10 mr-0" />
          <h1 className="text-1xl font-bold">Connectify</h1>
        </div>
        <SearchBar /> 

          {/* Hamburger Menu Button */}
          <div className="md:hidden">
            <button onClick={toggleSidebar} className="text-gray-800">
              <FaBars />
            </button>
          </div>
        </header>

        {/* Content Area */}
        <main className="flex-grow p-6 bg-gray-100 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
