import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { FaVideo, FaPhoneSlash } from "react-icons/fa";

const VideoCall = () => {
    const { userId } = useParams();
    const currentUser  = useSelector((state) => state.user.user);
    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);
    const peerConnection = useRef(null);
    const ws = useRef(null);
    const [isCallActive, setIsCallActive] = useState(false);
    const [isCallReceived, setIsCallReceived] = useState(false);
    const [isCaller, setIsCaller] = useState(true);
    const token = useSelector((state) => state.auth.token);
    
    useEffect(()=>{
        startLocalVideo()
    },[userId])

    useEffect(() => {
        const wsProtocol = window.location.protocol === 'https:' ? 'wss' : 'ws';
        ws.current = new WebSocket(`${wsProtocol}://localhost:8000/chat/${userId}/?token=${token}`);

        ws.current.onopen = () => {
            console.log("WebSocket connection established.");
        };

        ws.current.onmessage = (message) => {
            const data = JSON.parse(message.data);
            console.log("Message received from WebSocket:", data);

            switch (data.action) {
                case 'video_call_offer':
                    setIsCallReceived(true);
                    setIsCaller(false);
                    handleOffer(data.offer);
                    break;
                case 'video_call_answer':
                    handleVideoCallAnswered(data.answer);
                    break;
                case 'ice_candidate':
                    handleICECandidate(data.candidate);
                    break;
                case 'call_accepted':
                    setIsCallActive(true);
                    setIsCallReceived(false);
                    break;
                case 'call_rejected':
                    setIsCallReceived(false);
                    break;
                case 'end_call':
                    endCall();
                    break;
                default:
                    console.warn(`Unhandled action type: ${data.action}`);
                    break;
            }
        };

        return () => {
            if (ws.current) {
                ws.current.close();
                console.log("WebSocket connection closed.");
            }
        };
    }, [userId]);

    const startLocalVideo = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
            if (localVideoRef.current) {
                localVideoRef.current.srcObject = stream;
                stream.getTracks().forEach(track => {
                    if (peerConnection.current) {
                        peerConnection.current.addTrack(track, stream);
                    }
                });
            }
        } catch (error) {
            console.error("Error accessing local media: ", error);
        }
    };

    const createPeerConnection = () => {
        peerConnection.current = new RTCPeerConnection({
            iceServers: [{ urls: 'stun:stun.l.google.com:19302' }]
        });

        peerConnection.current.onicecandidate = (event) => {
            if (event.candidate) {
                ws.current.send(JSON.stringify({
                    action: 'ice_candidate',
                    candidate: event.candidate,
                    recipient_id: userId
                }));
            }
        };

        peerConnection.current.ontrack = (event) => {
            if (remoteVideoRef.current) {
                remoteVideoRef.current.srcObject = event.streams[0];
            }
        };
    };

    const handleOffer = async (offer) => {
        if (!peerConnection.current) createPeerConnection();
        await peerConnection.current.setRemoteDescription(new RTCSessionDescription(offer));
    };

    const acceptCall = async () => {
        if (!peerConnection.current || isCaller) return;

        await startLocalVideo();
        const answer = await peerConnection.current.createAnswer();
        await peerConnection.current.setLocalDescription(answer);

        ws.current.send(JSON.stringify({
            action: 'video_call_answer',
            answer: peerConnection.current.localDescription,
            recipient_id: userId
        }));

        setIsCallActive(true);
        setIsCallReceived(false);
    };

    const handleVideoCallAnswered = async (answer) => {
        if (!peerConnection.current) {
            createPeerConnection();
        }
        await peerConnection.current.setRemoteDescription(new RTCSessionDescription(answer));
        setIsCallActive(true);
    };

    const handleICECandidate = async (candidate) => {
        if (candidate) {
            await peerConnection.current.addIceCandidate(new RTCIceCandidate (candidate));
        }
    };

    const startCall = async () => {
        if (!peerConnection.current) createPeerConnection();
        await startLocalVideo();
        const offer = await peerConnection.current.createOffer();
        await peerConnection.current.setLocalDescription(offer);
        ws.current.send(JSON.stringify({
            action: 'video_call_offer',
            offer: offer,
            recipient_id: userId
        }));
        setIsCallActive(true);
    };

    const endCall = () => {
        if (ws.current) {
            ws.current.send(JSON.stringify({
                action: 'end_call',
                sender_id: currentUser.id,
                recipient_id: userId
            }));
        }

        if (peerConnection.current) {
            peerConnection.current.close();
            peerConnection.current = null;
        }

        if (localVideoRef.current && localVideoRef.current.srcObject) {
            const stream = localVideoRef.current.srcObject;
            const tracks = stream.getTracks();
            tracks.forEach(track => track.stop());
            localVideoRef.current.srcObject = null;
        }

        if (remoteVideoRef.current) {
            remoteVideoRef.current.srcObject = null;
        }

        setIsCallActive(false);
        setIsCallReceived(false);
    };

    return (
        <div>
           
            <div style={{ marginLeft: '76%', width: '90%' }}>
                {isCaller && <video ref={localVideoRef} autoPlay muted style={{ display: 'block', width: '50%', textAlign: 'center' }} />}
            </div>

            {!isCallActive && isCallReceived && !isCaller && (
                <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex justify-center items-center z-50">
                    <div className="bg-white rounded-lg shadow-lg p-6 w-80 text-center">
                        <p className="text-lg font-semibold mb-4">Incoming call from {currentUser.username}...</p>
                        <div className="flex justify-center space-x-4">
                            <button
                                onClick={acceptCall}
                                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200"
                            >
                                Accept
                            </button>
                            <button
                                onClick={endCall}
                                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition duration-200"
                            >
                                Decline
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {!isCallActive && !isCallReceived && isCaller && (
                <div className="flex justify-center items-center">
                    <button style={{ marginLeft: '762%', marginBottom: '21%' }}
                        onClick={startCall}
                        className="bg-blue-400 p-2 rounded-full text-white hover:bg-blue-400 shadow-lg transition-all duration-300"
                    >
                        <FaVideo className="w-8 h-8" />
                    </button>
                </div>
            )}

            {isCallActive && (
                <button style={{ marginLeft: "94%" }}
                    onClick={endCall}
                    className="bg-red-500 p-2 rounded-full text-white hover:bg-red-600 shadow-lg transition-all duration-300"
                >
                    <FaPhoneSlash className="w-6 h-6" /> {/* End Call Icon */}
                </button>
            )}
        </div>
    );
};

export default VideoCall;
