import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { BASE_URL } from '../config';
import { showErrorToast } from './ErroToast';
import { Link } from 'react-router-dom';

const ProfileDetailsPage = () => {
  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);

  const [profileData, setProfileData] = useState({});
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfileData(response.data);
      } catch (err) {
        setError('An error occurred while fetching the profile.');
        showErrorToast('An error occurred while fetching the profile.');
      }
    };

    if (user && token) fetchProfile();
  }, [user, token]);

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-md rounded-md mt-8">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-2xl font-semibold">Profile Details</h2>
        <Link to="/profile" className="text-blue-500 hover:text-blue-700">
          Back
        </Link>
      </div>

      {error && (
        <div className="text-red-500 text-center mb-4">{error}</div>
      )}

      {/* Profile Details */}
      <div className="mb-8">
        {/* Username Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Username</label>
          <div className="mt-1 p-2 block w-full border border-gray-300 rounded-md bg-gray-100">
            {profileData.username}
          </div>
        </div>

        {/* First Name Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">First Name</label>
          <div className="mt-1 p-2 block w-full border border-gray-300 rounded-md bg-gray-100">
            {profileData.first_name}
          </div>
        </div>

        {/* Email Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Email</label>
          <div className="mt-1 p-2 block w-full border border-gray-300 rounded-md bg-gray-100">
            {profileData.email}
          </div>
        </div>

        {/* Bio Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Bio</label>
          <div className="mt-1 p-2 block w-full border border-gray-300 rounded-md bg-gray-100">
            {profileData.bio}
          </div>
        </div>

        {/* Date of Birth Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Date of Birth</label>
          <div className="mt-1 p-2 block w-full border border-gray-300 rounded-md bg-gray-100">
            {profileData.dob}
          </div>
        </div>

        {/* Mobile Number Field */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Mobile Number</label>
          <div className="mt-1 p-2 block w-full border border-gray-300 rounded-md bg-gray-100">
            {profileData.mobile}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileDetailsPage;
