import React, { useState, useRef, useEffect } from 'react';
import videojs from 'video.js';
import { FFmpeg } from '@ffmpeg/ffmpeg'; // Make sure to use fetchFile from the correct import

const ffmpeg = FFmpeg({ log: true });

const VideoTrimmer = ({ videoUrl, onSave, onClose }) => {
  const playerRef = useRef(null);
  const [videoPlayer, setVideoPlayer] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [trimmedVideoUrl, setTrimmedVideoUrl] = useState(null);

  useEffect(() => {
    if (playerRef.current && !videoPlayer) {
      const player = videojs(playerRef.current, {
        controls: true,
        autoplay: false,
        preload: 'auto',
      });
      setVideoPlayer(player);
    }

    return () => {
      if (videoPlayer) {
        videoPlayer.dispose();
      }
    };
  }, [playerRef, videoPlayer]);

  const handleSave = async () => {
    setIsProcessing(true);
    
    try {
      if (!ffmpeg.isLoaded()) {
        await ffmpeg.load(); // Ensure FFmpeg is loaded
      }

      const { start, end } = { start: '00:00:00', end: '00:01:00' }; // Example values to trim video to 1 minute

      // Fetch the video file from URL
      const response = await fetch(videoUrl);
      const videoBuffer = await response.arrayBuffer();
      
      ffmpeg.FS('writeFile', 'input.mp4', new Uint8Array(videoBuffer));

      // Run FFmpeg command to trim the video
      await ffmpeg.run(
        '-i', 'input.mp4',
        '-ss', start,
        '-to', end,
        '-c', 'copy',
        'output.mp4'
      );

      const trimmedData = ffmpeg.FS('readFile', 'output.mp4');
      const trimmedBlob = new Blob([trimmedData.buffer], { type: 'video/mp4' });
      const trimmedUrl = URL.createObjectURL(trimmedBlob);

      setTrimmedVideoUrl(trimmedUrl); // Set the trimmed video URL
      onSave(trimmedUrl); // Pass the trimmed video URL to parent component
    } catch (error) {
      console.error('Error trimming video:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="relative">
      <video ref={playerRef} className="video-js" src={trimmedVideoUrl || videoUrl} />
      <button
        onClick={handleSave}
        disabled={isProcessing}
        className="absolute bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
      >
        {isProcessing ? 'Processing...' : 'Trim Video'}
      </button>
      <button
        onClick={onClose}
        className="absolute top-4 right-4 bg-gray-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-gray-600"
      >
        Close
      </button>
    </div>
  );
};

export default VideoTrimmer;
