// src/layouts/Layout.jsx
import React, { useState } from 'react';
import { Outlet } from 'react-router-dom'; // For dynamically loading components in the center
import { FaBars, FaTimes } from 'react-icons/fa'; // Icons for the collapse functionality

const Layout = () => {
  const [isNavCollapsed, setIsNavCollapsed] = useState(false);

  const toggleNavbar = () => {
    setIsNavCollapsed(!isNavCollapsed);
  };

  return (
    <div className="flex h-screen">
      {/* Collapsible Navbar */}
      <aside className={`bg-gray-800 text-white ${isNavCollapsed ? 'w-16' : 'w-64'} transition-width duration-300`}>
        <div className="flex justify-between items-center p-4">
          <button onClick={toggleNavbar} className="text-xl">
            {isNavCollapsed ? <FaBars /> : <FaTimes />}
          </button>
        </div>
        <nav className="flex flex-col space-y-2 mt-4 px-2">
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">Dashboard</a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">Profile</a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">Settings</a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">Logout</a>
        </nav>
      </aside>

      {/* Main Content Area */}
      <div className="flex flex-col flex-grow">
        {/* Header */}
        <header className="bg-white shadow-md py-4 px-6">
          <h1 className="text-2xl font-semibold text-gray-800">My Application</h1>
        </header>

        {/* Content Area */}
        <main className="flex-grow p-6 bg-gray-100 overflow-auto">
          <Outlet /> {/* This will render the routed components */}
        </main>
      </div>
    </div>
  );
};

export default Layout;
