// features/posts/profileSlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  profile: null,
  posts: [],
  bookmarks: [],
  likedPosts: {},
  comments: {},
  loading: false,
  error: null,
  modalIsOpen: false,
  modalImage: '',
  openCommentId: null,
  imageType: '',
  newComment: "",
  replyingToCommentId: null,
  commentText: '',
  followerCount: 0,
  followingCount: 0,
  bookmarkPosts: {},
  showEmojiPicker: false,
};

const profileSlice = createSlice({
  name: 'profile',
  initialState,
  reducers: {
    setProfile: (state, action) => {
      state.profile = action.payload;
    },
    setPosts: (state, action) => {
      state.posts = action.payload;
    },
    setBookmarks: (state, action) => {
      state.bookmarks = action.payload;
    },
    setLikedPosts: (state, action) => {
      state.likedPosts = action.payload;
    },
    setComments: (state, action) => {
      state.comments = action.payload;
    },
    setLoading: (state, action) => {
      state.loading = action.payload;
    },
    setError: (state, action) => {
      state.error = action.payload;
    },
    setModalIsOpen: (state, action) => {
      state.modalIsOpen = action.payload;
    },
    setModalImage: (state, action) => {
      state.modalImage = action.payload;
    },
    setOpenCommentId: (state, action) => {
      state.openCommentId = action.payload;
    },
    setImageType: (state, action) => {
      state.imageType = action.payload;
    },
    setNewComment: (state, action) => {
      state.newComment = action.payload;
    },
    setReplyingToCommentId: (state, action) => {
      state.replyingToCommentId = action.payload;
    },
    setCommentText: (state, action) => {
      state.commentText = action.payload;
    },
    setFollowerCount: (state, action) => {
      state.followerCount = action.payload;
    },
    setFollowingCount: (state, action) => {
      state.followingCount = action.payload;
    },
    setBookmarkPosts: (state, action) => {
      state.bookmarkPosts = action.payload;
    },
    setShowEmojiPicker: (state, action) => {
      state.showEmojiPicker = action.payload;
    },
  },
});

export const profileReducer = profileSlice.reducer;
export const profileActions = profileSlice.actions;
