import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchNotifications, markNotificationAsRead } from '../features/auth/notificationSlice';
import { useNavigate } from 'react-router-dom';

const NotificationList = ({ onClose }) => {
  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);
  const { notifications = [], loading, error } = useSelector((state) => state.notifications);
  const navigate = useNavigate();

  useEffect(() => {
    dispatch(fetchNotifications({ token }));
  }, [dispatch, token]);

  const handleNotificationClick = (notificationId) => {
    dispatch(markNotificationAsRead({ notificationId, token }));
    onClose(); // Close the notification list after clicking
  };

  const handleViewAllNotifications = () => {
    navigate('/notifications');
    onClose();
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="absolute right-0 mt-2 w-80 bg-white shadow-lg rounded-md overflow-hidden">
      {notifications.length > 0 ? (
        <ul>
          {notifications.slice(0, 10).map((notification) => (
            <li
              key={notification.id}
              className={`p-4 border-b text-gray-800 cursor-pointer ${
                notification.is_read ? 'bg-gray-100' : 'bg-white'
              }`}
              onClick={() => handleNotificationClick(notification.id)}
            >
              <p className="font-semibold">{notification.sender_username}</p>
              <p>{notification.notification_type} your post</p>
            </li>
          ))}
        </ul>
      ) : (
        <li className="p-4 text-gray-600">No new notifications</li>
      )}
      {notifications.length > 10 && (
        <button
          className="w-full p-4 text-center text-blue-600 hover:bg-gray-200"
          onClick={handleViewAllNotifications}
        >
          View All Notifications
        </button>
      )}
    </div>
  );
};

export default NotificationList;
