import React, { useEffect } from 'react';
import { Bar, Line } from 'react-chartjs-2';
import { useDispatch, useSelector } from 'react-redux';
import { fetchWeeklyUserRegistration,fetchEngagementStats,fetchWeeklyPostActivity } from '../features/auth/dashBoardSlice';


const AdminDashboardReports = () => {
  const dispatch = useDispatch();
  const {
    userRegistrationData,
    engagementData,
    postActivityData,
    loading,
    error,
  } = useSelector((state) => state.dashboard);

  useEffect(() => {
    dispatch(fetchWeeklyUserRegistration());
    dispatch(fetchEngagementStats());
    dispatch(fetchWeeklyPostActivity());
  }, [dispatch]);

  // Prepare data for the charts
  const userRegistrationChartData = {
    labels: userRegistrationData.map((item) => item.day),
    datasets: [
      {
        label: 'User Registrations',
        data: userRegistrationData.map((item) => item.count),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const postActivityChartData = {
    labels: postActivityData.likes_per_day?.map((item) => item.day),
    datasets: [
      {
        label: 'Likes',
        data: postActivityData.likes_per_day?.map((item) => item.count),
        borderColor: 'rgba(75, 192, 192, 1)',
        fill: false,
      },
      {
        label: 'Comments',
        data: postActivityData.comments_per_day?.map((item) => item.count),
        borderColor: 'rgba(153, 102, 255, 1)',
        fill: false,
      },
    ],
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-bold text-center mb-6">Admin Dashboard</h2>

      {loading && <p className="text-center text-blue-500">Loading...</p>}
      {error && <p className="text-center text-red-500">Error: {error}</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* User Registrations */}
        <div className="bg-white p-6 shadow rounded">
          <h3 className="text-lg font-semibold mb-4">Weekly User Registrations</h3>
          <Bar data={userRegistrationChartData} />
        </div>

        {/* Engagement Stats */}
        <div className="bg-white p-6 shadow rounded">
          <h3 className="text-lg font-semibold mb-4">User Engagement Stats</h3>
          <ul className="space-y-2">
            <li>Total Likes: {engagementData.total_likes}</li>
            <li>Total Comments: {engagementData.total_comments}</li>
            <li>Total Follows: {engagementData.total_follows}</li>
          </ul>
        </div>

        {/* Post Activity */}
        <div className="bg-white p-6 shadow rounded col-span-1 md:col-span-2">
          <h3 className="text-lg font-semibold mb-4">Weekly Post Activity</h3>
          <Line data={postActivityChartData} />
        </div>
      </div>
    </div>
  );
};

export default AdminDashboardReports;
