// VideoTrimmer.js

import React, { useEffect, useRef, useState } from 'react';
import Nouislider from 'nouislider-react';
import 'nouislider/distribute/nouislider.css';
import './VideoTrimmer.css';

let ffmpeg;

const VideoTrimmer = ({ onTrimComplete }) => {
  const [videoDuration, setVideoDuration] = useState(0);
  const [endTime, setEndTime] = useState(0);
  const [startTime, setStartTime] = useState(0);
  const [videoSrc, setVideoSrc] = useState('');
  const [videoFile, setVideoFile] = useState(null);
  const [isScriptLoaded, setIsScriptLoaded] = useState(false);
  const [videoTrimmedUrl, setVideoTrimmedUrl] = useState('');
  const videoRef = useRef();

  // Load ffmpeg script
  const loadScript = (src) => {
    return new Promise((resolve) => {
      const script = document.createElement('script');
      script.src = src;
      script.async = true;
      script.onload = () => resolve();
      document.head.appendChild(script);
    });
  };

  useEffect(() => {
    loadScript('https://cdn.jsdelivr.net/npm/@ffmpeg/ffmpeg@0.11.2/dist/ffmpeg.min.js')
      .then(() => {
        ffmpeg = window.FFmpeg.createFFmpeg({ log: true });
        return ffmpeg.load();
      })
      .then(() => {
        setIsScriptLoaded(true);
      });
  }, []);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setVideoFile(file);
      setVideoSrc(url);
    }
  };

  const handlePlay = () => {
    if (videoRef.current) {
      videoRef.current.play();
    }
  };

  const handlePauseVideo = (e) => {
    const currentTime = Math.floor(e.currentTarget.currentTime);
    if (currentTime >= endTime) {
      e.currentTarget.pause();
    }
  };

  const convertToHHMMSS = (seconds) => {
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = Math.floor(seconds % 60);
    return [h, m, s].map((v) => (v < 10 ? `0${v}` : v)).join(':');
  };

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.onloadedmetadata = () => {
        setVideoDuration(videoRef.current.duration);
        setEndTime(videoRef.current.duration);
      };
    }
  }, [videoSrc]);

  const updateOnSliderChange = (values, handle) => {
    setVideoTrimmedUrl('');
    const value = Math.round(values[handle]);
    if (handle === 0) {
      setStartTime(value);
      if (videoRef.current) {
        videoRef.current.currentTime = value;
      }
    } else {
      setEndTime(value);
    }
  };

  const handleTrim = async () => {
    if (isScriptLoaded && videoFile) {
      const { name, type } = videoFile;
      ffmpeg.FS('writeFile', name, await window.FFmpeg.fetchFile(videoFile));

      await ffmpeg.run(
        '-i', name,
        '-ss', convertToHHMMSS(startTime),
        '-to', convertToHHMMSS(endTime),
        '-acodec', 'copy',
        '-vcodec', 'copy',
        `output.${type.split('/')[1]}`
      );

      const data = ffmpeg.FS('readFile', `output.${type.split('/')[1]}`);
      const url = URL.createObjectURL(new Blob([data.buffer], { type }));
      setVideoTrimmedUrl(url);
      onTrimComplete(url); // Pass trimmed URL to parent component
    }
  };

  return (
    <div className="video-trimmer">
      <input type="file" accept="video/*" onChange={handleFileUpload} />
      {videoSrc && (
        <>
          <video src={videoSrc} ref={videoRef} onTimeUpdate={handlePauseVideo} controls />
          <Nouislider
            range={{ min: 0, max: videoDuration }}
            start={[0, videoDuration]}
            connect
            step={1}
            onUpdate={updateOnSliderChange}
          />
          <div>
            Start: {convertToHHMMSS(startTime)} &nbsp; End: {convertToHHMMSS(endTime)}
          </div>
          <button onClick={handlePlay}>Play</button>
          <button onClick={handleTrim}>Trim</button>
          {videoTrimmedUrl && (
            <div>
              <h4>Trimmed Video:</h4>
              <video src={videoTrimmedUrl} controls />
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default VideoTrimmer;
