import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { BASE_URL } from '../config';

const VerificationWaitingPage = () => {
  const { uidb64, token } = useParams();
  const [status, setStatus] = useState('pending');
  const navigate = useNavigate();

  useEffect(() => {
    if (!uidb64 || !token) {
      setStatus('invalid');
      return;
    }

    const checkTokenValidity = async () => {
      try {
        // Call to check if the token is valid
        const response = await axios.get(`${BASE_URL}/api/users/check-token/${uidb64}/${token}/`);
        const { status } = response.data;

        if (status === 'valid') {
          // Token is valid, now trigger the activation
          activateUser();
        } else {
          setStatus('invalid');
        }
      } catch (error) {
        setStatus('invalid');
      }
    };

    const activateUser = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/activate/${uidb64}/${token}/`);
        const { status } = response.data;

        if (status === 'activated' || status === 'already_activated') {
          setStatus('verified');
        } else {
          setStatus('invalid');
        }
      } catch (error) {
        setStatus('invalid');
      }
    };

    // First check if the token is valid
    checkTokenValidity();
  }, [uidb64, token]);

  useEffect(() => {
    if (status === 'verified') {
      navigate('/login');
    } else if (status === 'invalid') {
      navigate('/error');
    }
  }, [status, navigate]);

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-200 to-blue-400 p-6">
      <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-8 flex flex-col items-center">
        {status === 'pending' && (
          <div className="text-center">
            <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 mb-4"></div>
            <h2 className="text-2xl font-semibold text-gray-800">Verifying Your Email</h2>
            <p className="text-gray-600">Please wait while we verify your email address...</p>
          </div>
        )}
        {status === 'invalid' && (
          <div className="text-center">
            <svg
              className="w-16 h-16 mx-auto mb-4 text-red-500"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M5.05 4.05a7.96 7.96 0 0111.89 0A7.96 7.96 0 0120.95 12a7.96 7.96 0 01-11.89 0A7.96 7.96 0 015.05 4.05z" />
            </svg>
            <h2 className="text-2xl font-semibold text-red-600">Invalid Verification Link</h2>
            <p className="text-gray-600">The verification link is invalid or has expired. Please try again.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default VerificationWaitingPage;
