import React from 'react';
import Modal from 'react-modal'; // Assuming you are using 'react-modal' for the modal implementation

// Make sure to set the app root for accessibility reasons with react-modal
Modal.setAppElement('#root');

const ParticipantModal = ({ isOpen, onClose, participants }) => {
  return (
    <Modal isOpen={isOpen} onRequestClose={onClose} contentLabel="Participants Modal">
      <h2>Participants</h2>
      <button onClick={onClose} style={{ float: 'right', cursor: 'pointer' }}>Close</button>
      <ul>
        {participants.map((participant) => (
          <li key={participant.id} className="participant-item">
            <img src={participant.profile_pic} alt={participant.username} className="participant-img" />
            <span>{participant.username}</span>
          </li>
        ))}
      </ul>
    </Modal>
  );
};

export default ParticipantModal;
