import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { toast } from 'react-toastify';
import { createAnnouncement, resetAnnouncementState } from '../features/auth/announcementSlice';

const AdminAnnouncementPage = () => {
  const [content, setContent] = useState('');
  const dispatch = useDispatch();
  const { loading, success, error } = useSelector((state) => state.announcement);
  const token = useSelector((state) => state.auth.token); // assuming you are storing the token in local storage

  useEffect(() => {
    if (success) {
      toast.success('Announcement created successfully!');
      setContent(''); // Clear input after successful submission
      dispatch(resetAnnouncementState());
    }
    if (error) {
      toast.error(`Error: ${error}`);
    }
  }, [success, error, dispatch]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!content.trim()) {
      toast.warn('Content cannot be empty!');
      return;
    }
    dispatch(createAnnouncement({ content, token }));
  };

  return (
    <div className="max-w-xl mx-auto p-8">
      <h1 className="text-2xl font-bold mb-6">Create Admin Announcement</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="content" className="block text-gray-700 text-sm font-bold mb-2">
            Announcement Content
          </label>
          <textarea
            id="content"
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            rows={5}
          ></textarea>
        </div>

        <button
          type="submit"
          className={`bg-blue-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline ${
            loading ? 'opacity-50 cursor-not-allowed' : ''
          }`}
          disabled={loading}
        >
          {loading ? 'Sending...' : 'Create Announcement'}
        </button>
      </form>
    </div>
  );
};

export default AdminAnnouncementPage;
