import React from 'react';
import './App.css';
import Login from './components/Login';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import store from './store';
import { Provider } from 'react-redux';
import SignupPage from './pages/SignupPage';
import Layout from './layouts/Layouts';
import { ToastContainer } from 'react-toastify';
import VerificationWaitingPage from './pages/VerificationWaitingPage ';
import AdminDashboard from './admin/dashboard';
import ProtectedRoute from './components/ProtectedRoute';
import PasswordResetRequest from './components/PasswordResetRequest';
import PasswordReset from './components/PasswordReset';
import {GoogleOAuthProvider} from '@react-oauth/google';

function App() {
  return (

    <Provider store={store}>
      <GoogleOAuthProvider clientId="586931689260-6gkpnir7m3k6nlpdshc09cn12pkgcn79.apps.googleusercontent.com">
    <Router>
      <ToastContainer/>
        <Routes>
        <Route path="/" element={<Layout />}/>
        <Route path="/login" element={<Login />} />

        <Route path="/signup" element={<SignupPage />} />
        <Route path="/verification-waiting/:uidb64/:token" element={<VerificationWaitingPage />} />
        <Route
            path="/admin/dashboard"
            element={
              <ProtectedRoute isAdminRoute={true}>
                <AdminDashboard />
              </ProtectedRoute>
            }
          />
           <Route path="/password-reset-request" element={<PasswordResetRequest />} />
           <Route path="/password-reset/:uidb64/:token" element={<PasswordReset />} />
        </Routes>

    </Router>
    </GoogleOAuthProvider>
</Provider>



  );
}

export default App;
