import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { BASE_URL } from '../../config';



export const fetchBookmarkStatus = createAsyncThunk(
  'bookmarkPosts/fetchBookmarkStatus',
  async ({ posts, token }) => {
    const bookmarkPosts = {};
    for (let post of posts) {
      const response = await fetch(`${BASE_URL}/api/posts/${post.id}/check_bookmarked/`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const result = await response.json();
        bookmarkPosts[post.id] = result.bookmarked; // Assume the response contains { bookmarked: true/false }
      }
    }
    return bookmarkPosts;
  }
);

export const handleBookmark = createAsyncThunk(
  'bookmarkPosts/handleBookmark',
  async ({ postId, token, isBookmarked }, { dispatch }) => {
    if (isBookmarked) {
      // If already bookmarked, unbookmark the post
      await axios.delete(`${BASE_URL}/api/bookmarks/${postId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return { postId, bookmarked: false };
    } else {
      // If not bookmarked, bookmark the post
      await axios.post(`${BASE_URL}/api/bookmarks/`, { post: postId }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return { postId, bookmarked: true };
    }
  }
);

const bookmarkPostsSlice = createSlice({
  name: 'bookmarkPosts',
  initialState: {},
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchBookmarkStatus.fulfilled, (state, action) => {
        return { ...state, ...action.payload };
      })
      .addCase(handleBookmark.fulfilled, (state, action) => {
        state[action.payload.postId] = action.payload.bookmarked;
      });
  },
});

export default bookmarkPostsSlice.reducer;
