import React from 'react';
import { useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';

const Suggestion = ({ suggestions }) => {
  const user = useSelector((state) => state.user.user);
  const navigate = useNavigate();
  const limitedSuggestions = suggestions.slice(0, 6);

  if (!limitedSuggestions.length) return null;

  return (
    <div className="w-full max-w-sm mx-auto p-4 bg-white rounded-lg shadow-md">
      <div className="flex items-center justify-between mb-4">
        <h3 className="font-semibold text-gray-800">Suggestions for you</h3>
        <button
          onClick={() => navigate('/see-all')}
          className="text-blue-500 hover:underline text-sm"
        >
          See All
        </button>
      </div>
      <ul>
        {limitedSuggestions.map((suggestion, index) => (
          <li
            key={suggestion.id}
            className={`flex items-center justify-between p-2 mb-2 ${index < suggestions.length - 1 && 'border-b'} border-gray-300`}
          ><Link
          to={`/profileview/${suggestion.id}`}
          className="text-blue-500 hover:text-blue-700"
          title="Profile"
          key={suggestion.id}
        >
            <div className="flex items-center">
            
              <img
                className="w-10 h-10 rounded-full mr-3 object-cover"
                src={suggestion.profile_pic || 'https://via.placeholder.com/800x500'}
                alt={suggestion.username}
              />
              <div>
                <p key={suggestion.id} className="text-sm font-medium">{suggestion.username}</p>
              </div>
              
            </div>
            </Link>
            <button className="text-sm text-blue-500 border border-blue-500 px-3 py-1 rounded hover:bg-blue-500 hover:text-white">
              Follow
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Suggestion;
