// src/components/SignupForm.jsx
import React, { useState } from 'react';

const SignupForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    first_name: '',
    email: '',
    bio: '',
    dob: '',
    mobile: '',
    profile_pic: null,
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      profile_pic: e.target.files[0],
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Creating form data for backend API
    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    console.log('Signup data:', data); // Replace with API call
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="flex flex-col gap-6 p-8 bg-white shadow-lg rounded-lg max-w-lg w-full"
    >
      <h2 className="text-2xl font-bold text-center text-gray-800">Sign Up</h2>
      
      <input
        type="text"
        name="username"
        placeholder="Username"
        value={formData.username}
        onChange={handleChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
        required
      />
      <input
        type="text"
        name="first_name"
        placeholder="First Name"
        value={formData.first_name}
        onChange={handleChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
        required
      />
      <textarea
        name="bio"
        placeholder="Bio"
        value={formData.bio}
        onChange={handleChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 resize-none h-24"
      />
      <input
        type="date"
        name="dob"
        placeholder="Date of Birth"
        value={formData.dob}
        onChange={handleChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
      />
      <input
        type="tel"
        name="mobile"
        placeholder="Mobile Number"
        value={formData.mobile}
        onChange={handleChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
      />
      <input
        type="file"
        name="profile_pic"
        accept="image/*"
        onChange={handleFileChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={formData.password}
        onChange={handleChange}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
        required
      />
      <button
        type="submit"
        className="bg-blue-500 text-white py-3 rounded-lg hover:bg-blue-600 transition duration-200"
      >
        Sign Up
      </button>
    </form>
  );
};

export default SignupForm;
