import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa'; // Import the camera icon
import { Link } from 'react-router-dom';

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([
    { id: 1, content: 'Just started a new project! Excited to share the progress.' },
    { id: 2, content: 'Attended a fantastic workshop today. Learned so much!' },
    { id: 3, content: 'Feeling motivated to code more today!' },
  ]);
  const [newPost, setNewPost] = useState('');
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);

  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
        headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    if (user && token) {
      fetchProfile();
    }
  }, [user, token]);

  const handleFileChange = (event, type) => {
    const file = event.target.files[0];
    if (type === 'cover') {
      setCoverImage(file);
    } else {
      setProfileImage(file);
    }
  };

  const handleImageUpload = async (type) => {
    const formData = new FormData();
    formData.append(type === 'cover' ? 'cover_pic' : 'profile_pic', type === 'cover' ? coverImage : profileImage);

    try {
      await axios.patch(`${BASE_URL}/api/users/profile/${user?.id}/`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setCoverImage(null);
      setProfileImage(null);
      setProfile((prevProfile) => ({
        ...prevProfile,
        [type === 'cover' ? 'cover_pic' : 'profile_pic']: URL.createObjectURL(type === 'cover' ? coverImage : profileImage),
      }));
    } catch (err) {
      setError('An error occurred while uploading the image.');
    }
  };

  const handlePostSubmit = () => {
    const newDummyPost = { id: posts.length + 1, content: newPost };
    setPosts([newDummyPost, ...posts]);
    setNewPost('');
  };

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return <div className="text-center mt-4">Loading...</div>;
  }

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      {/* Cover Photo */}
      <div className="relative h-40 bg-cover bg-center" style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x200'})` }}>
        <label htmlFor="cover-upload" className="absolute top-2 right-2 cursor-pointer bg-white p-1 rounded-full">
          <FaCamera className="text-gray-600" />
        </label>
        <input id="cover-upload" type="file" onChange={(e) => handleFileChange(e, 'cover')} className="hidden" />
        
        {/* Profile Picture */}
        <div className="absolute bottom-0 left-4 transform -translate-y-1/2">
          <img
            src={profile.profile_pic || 'https://via.placeholder.com/100'}
            alt="Profile"
            className="w-20 h-20 rounded-full border-4 border-white"
          />
          <label htmlFor="profile-upload" className="absolute bottom-2 left-16 cursor-pointer bg-white p-1 rounded-full">
            <FaCamera className="text-gray-600" />
          </label>
          <input id="profile-upload" type="file" onChange={(e) => handleFileChange(e, 'profile')} className="hidden" />
        </div>
      </div>

      {/* Profile Details */}
      <div className="p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">{profile.first_name}</h2>
          <h2 className="text-sm text-white opacity-75 shadow-sm">{`@${profile.username}`}</h2>
        <Link
        to="/edit-profile"
        className="text-blue-500 border border-blue-500 px-4 py-1 rounded-full hover:bg-blue-500 hover:text-white"
        >
        Edit Profile
        </Link>
        </div>
        <p className="text-gray-600">{profile.bio || 'No bio available'}</p>

        {/* Post Box */}
        <div className="mt-6">
          <textarea
            className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500"
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
          />
          <button onClick={handlePostSubmit} className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600">
            Post
          </button>
        </div>

        {/* Feed Section */}
        <div className="mt-8">
          <h3 className="text-xl font-semibold mb-4">Your Posts</h3>
          {posts.length > 0 ? (
            posts.map((post) => (
              <div key={post.id} className="border p-4 mb-4 rounded-md shadow-sm">
                <p className="text-gray-800">{post.content}</p>
              </div>
            ))
          ) : (
            <p className="text-gray-500">No posts available</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
