import React, { useRef, useState } from 'react';
import 'video.js/dist/video-js.css';
import videojs from 'video.js';
import { createFFmpeg, fetchFile } from '@ffmpeg/ffmpeg';

const VideoTrimmer = ({ videoUrl, onSave, onClose }) => {
  const playerRef = useRef(null);
  const [videoPlayer, setVideoPlayer] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [ffmpeg] = useState(createFFmpeg({ log: true })); // Create FFmpeg instance

  React.useEffect(() => {
    if (playerRef.current && !videoPlayer) {
      const player = videojs(playerRef.current, {
        controls: true,
        autoplay: false,
        preload: 'auto',
      });
      setVideoPlayer(player);
    }
    return () => {
      if (videoPlayer) {
        videoPlayer.dispose();
      }
    };
  }, [playerRef, videoPlayer]);

  // Function to handle video trimming
  const handleSave = async () => {
    setIsProcessing(true);
    try {
      await ffmpeg.load(); // Load FFmpeg if not already loaded

      const { start, end } = { start: '00:00:00', end: '00:01:00' }; // Example trim values
      const fileData = await fetchFile(videoUrl);
      ffmpeg.FS('writeFile', 'input.mp4', fileData);

      // Run FFmpeg command to trim the video
      await ffmpeg.run(
        '-i', 'input.mp4',
        '-ss', start,
        '-to', end,
        '-c', 'copy',
        'output.mp4'
      );

      const data = ffmpeg.FS('readFile', 'output.mp4');
      const trimmedVideoBlob = new Blob([data.buffer], { type: 'video/mp4' });
      const trimmedVideoUrl = URL.createObjectURL(trimmedVideoBlob);

      onSave(trimmedVideoUrl); // Pass trimmed video URL to parent component
    } catch (error) {
      console.error('Error trimming video:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="relative">
      <video ref={playerRef} className="video-js" src={videoUrl} />
      <button
        onClick={handleSave}
        disabled={isProcessing}
        className="absolute bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
      >
        {isProcessing ? 'Processing...' : 'Save'}
      </button>
      <button
        onClick={onClose}
        className="absolute top-4 right-4 bg-gray-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-gray-600"
      >
        Close
      </button>
    </div>
  );
};

export default VideoTrimmer;
