// src/layouts/Layout.jsx
import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import { FaBars, FaChevronLeft, FaChevronRight } from 'react-icons/fa'; // Import minimize icons

const Layout = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // Sidebar state starts open by default

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside
        className={`bg-gray-800 text-white ${
          isSidebarOpen ? 'w-64' : 'w-16' // Adjust width based on state
        } transition-all duration-300 ease-in-out fixed md:relative inset-y-0 left-0 transform md:translate-x-0 z-30`}
      >
        {/* Sidebar Header with Toggle Button for Large Screens */}
        <div className="flex justify-between items-center p-4">
          {/* Minimize button for large screens */}
          <button
            onClick={toggleSidebar}
            className="text-white hidden md:block"
          >
            {isSidebarOpen ? <FaChevronLeft /> : <FaChevronRight />} {/* Toggle between icons */}
          </button>
          {/* Hamburger button for mobile */}
          <button onClick={toggleSidebar} className="text-white md:hidden">
            <FaBars />
          </button>
        </div>
        {isSidebarOpen && (
          <nav className="mt-8 px-2 space-y-2">
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Dashboard
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Profile
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Settings
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Logout
            </a>
          </nav>
        )}
      </aside>

      {/* Main Content */}
      <div
        className={`flex-1 flex flex-col ${
          isSidebarOpen ? 'ml-0' : 'ml-16' // Adjust margin based on sidebar state
        } transition-all duration-300 ease-in-out`}
      >
        {/* Header */}
        <header className="bg-white shadow-md flex items-center justify-between px-6 py-4">
          {/* Hamburger Button for Mobile */}
          <button onClick={toggleSidebar} className="md:hidden text-gray-800">
            <FaBars />
          </button>
          <h1 className="text-2xl font-bold">My Application</h1>
        </header>

        {/* Content Area */}
        <main className="flex-grow p-6 bg-gray-100 overflow-auto">
          <Outlet /> {/* This will render the routed components */}
        </main>
      </div>
    </div>
  );
};

export default Layout;
