// src/layouts/Layout.jsx
import React, { useState } from 'react';
import { Outlet } from 'react-router-dom'; // For dynamically loading components in the center
import { FaBars, FaChevronLeft  } from 'react-icons/fa'; // Icons for the collapse functionality

const Layout = () => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Sidebar state
  
    const toggleSidebar = () => {
      setIsSidebarOpen(!isSidebarOpen);
    };
  
    return (
        <div className="flex h-screen">
        {/* Sidebar */}
        <aside
          className={`bg-gray-800 text-white transform ${
            isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
          } md:translate-x-0 fixed md:relative inset-y-0 left-0 w-64 transition-transform duration-300 ease-in-out z-30`}
        >
          {/* Sidebar Header with Toggle Button for Large Screens */}
          <div className="flex justify-between items-center p-4">
            {/* Minimize button for large screens */}
            <button
              onClick={toggleSidebar}
              className="text-white md:block hidden"
            >
              <FaChevronLeft />
            </button>
            {/* Hamburger button for mobile */}
            <button onClick={toggleSidebar} className="text-white md:hidden">
              <FaBars />
            </button>
          </div>
          <nav className="mt-8 px-2 space-y-2">
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Dashboard
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Profile
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Settings
            </a>
            <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
              Logout
            </a>
          </nav>
        </aside>
  
        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* Header */}
          <header className="bg-white shadow-md flex items-center justify-between px-6 py-4">
            {/* Hamburger Button for Mobile */}
            <button onClick={toggleSidebar} className="md:hidden text-gray-800">
              <FaBars />
            </button>
            <h1 className="text-2xl font-bold">My Application</h1>
          </header>
  
          {/* Content Area */}
          <main className="flex-grow p-6 bg-gray-100 overflow-auto">
            <Outlet /> {/* This will render the routed components */}
          </main>
        </div>
      </div>
    );
  };
  
export default Layout;
