// components/Notification.js
import React from 'react';

const Notification = ({ message }) => {
    return (
        <div className="bg-gray-100 border border-gray-300 text-gray-800 rounded-md p-4 shadow-md mb-4 max-w-xs">
            {message}
        </div>
    );
};

export default Notification;
