import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';

Modal.setAppElement('#root');

const ProfilePage = () => {
  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);

  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([
    { id: 1, content: 'Just started a new project! Excited to share the progress.' },
    { id: 2, content: 'Attended a fantastic workshop today. Learned so much!' },
    { id: 3, content: 'Feeling motivated to code more today!' },
  ]);
  const [newPost, setNewPost] = useState('');
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [imageType, setImageType] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(response.data);
      } catch (err) {
        setError('An error occurred while fetching the profile.');
      }
    };

    if (user && token) fetchProfile();
  }, [user, token]);

  const handlePostChange = (e) => {
    setNewPost(e.target.value);
  };

  const handleAddPost = () => {
    if (newPost.trim()) {
      setPosts([...posts, { id: posts.length + 1, content: newPost }]);
      setNewPost('');
    }
  };

  const openModal = (image, type) => {
    setModalImage(image);
    setImageType(type);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-md rounded-md mt-8">
      <h2 className="text-2xl font-semibold mb-4">Profile Page</h2>

      {error && <div className="text-red-500 text-center mb-4">{error}</div>}

      {profile ? (
        <div>
          <div className="mb-4 flex items-center">
            <img
              src={profileImage || 'default-profile-image-url'}
              alt="Profile"
              className="w-20 h-20 rounded-full object-cover"
            />
            <FaCamera
              onClick={() => openModal(profileImage, 'profile')}
              className="ml-4 cursor-pointer text-gray-500"
            />
          </div>

          {/* Displaying Profile Information */}
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Username:</label>
            <p className="mt-1 text-gray-900">{profile.username}</p>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">First Name:</label>
            <p className="mt-1 text-gray-900">{profile.first_name}</p>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Email:</label>
            <p className="mt-1 text-gray-900">{profile.email}</p>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Bio:</label>
            <p className="mt-1 text-gray-900">{profile.bio}</p>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Date of Birth:</label>
            <p className="mt-1 text-gray-900">{profile.dob}</p>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Mobile Number:</label>
            <p className="mt-1 text-gray-900">{profile.mobile}</p>
          </div>

          <div className="mb-8">
            <Link to="/edit-profile" className="text-blue-500 hover:underline">
              Edit Profile
            </Link>
          </div>

          {/* Posts Section */}
          <div>
            <h3 className="text-xl font-semibold mb-2">Posts</h3>
            {posts.map((post) => (
              <div key={post.id} className="mb-2 p-2 border border-gray-300 rounded-md">
                {post.content}
              </div>
            ))}

            <div className="mt-4">
              <textarea
                value={newPost}
                onChange={handlePostChange}
                className="w-full p-2 border border-gray-300 rounded-md"
                placeholder="Write a new post..."
              />
              <button
                onClick={handleAddPost}
                className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
              >
                Add Post
              </button>
            </div>
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}

      {/* Modal for image upload */}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        <Cropper src={modalImage} style={{ height: 400, width: '100%' }} aspectRatio={1} />
        <button onClick={closeModal} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md">
          Close
        </button>
      </Modal>
    </div>
  );
};

export default ProfilePage;
