import React, { useState, useRef } from 'react';
import { FaSmile, FaCamera, FaVideo } from 'react-icons/fa';
import EmojiPicker from 'emoji-picker-react'; // Use default import

const PostBox = ({ onPostSubmit }) => {
  const [postContent, setPostContent] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const fileInputRef = useRef(null);

  const handleEmojiClick = (emojiData) => {
    const emoji = emojiData.emoji; // Access the emoji value correctly
    setPostContent((prevContent) => prevContent + emoji);
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const fileType = file.type.split('/')[0];
      if (fileType === 'image') {
        setSelectedImage(URL.createObjectURL(file));
        setSelectedVideo(null); // Clear the video if an image is selected
      } else if (fileType === 'video') {
        setSelectedVideo(URL.createObjectURL(file));
        setSelectedImage(null); // Clear the image if a video is selected
      }
    }
  };

  const handleSubmit = () => {
    if (!postContent.trim() && !selectedImage && !selectedVideo) {
      alert('Post content cannot be empty.');
      return;
    }

    onPostSubmit({
      content: postContent,
      image: selectedImage,
      video: selectedVideo,
    });

    // Clear the form
    setPostContent('');
    setSelectedImage(null);
    setSelectedVideo(null);
  };

  return (
    <div className="p-4 border rounded-md shadow-md mb-6 bg-white">
      <div className="flex items-center">
        <textarea
          className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500"
          placeholder="What's on your mind?"
          value={postContent}
          onChange={(e) => setPostContent(e.target.value)}
        />

        {/* Smiley Icon */}
        <div className="relative">
          <FaSmile
            className="ml-2 text-xl cursor-pointer"
            onClick={() => setShowEmojiPicker(!showEmojiPicker)}
          />
          {showEmojiPicker && (
            <div className="absolute top-full left-0 mt-2">
              {/* Emoji Picker Component */}
              <EmojiPicker onEmojiClick={handleEmojiClick} />
            </div>
          )}
        </div>
      </div>

      {/* Image/Video Preview */}
      {selectedImage && (
        <div className="mt-4">
          <img src={selectedImage} alt="Selected" className="max-w-full h-auto rounded-md" />
        </div>
      )}
      {selectedVideo && (
        <div className="mt-4">
          <video controls className="max-w-full h-auto rounded-md">
            <source src={selectedVideo} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}

      {/* Action Buttons */}
      <div className="flex items-center justify-between mt-4">
        <div className="flex space-x-4">
          {/* Image Upload */}
          <label className="cursor-pointer">
            <FaCamera className="text-xl" />
            <input
              type="file"
              accept="image/*"
              className="hidden"
              onChange={handleFileChange}
              ref={fileInputRef}
            />
          </label>

          {/* Video Upload */}
          <label className="cursor-pointer">
            <FaVideo className="text-xl" />
            <input
              type="file"
              accept="video/*"
              className="hidden"
              onChange={handleFileChange}
              ref={fileInputRef}
            />
          </label>
        </div>

        {/* Post Button */}
        <button
          onClick={handleSubmit}
          className="bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
        >
          Post
        </button>
      </div>
    </div>
  );
};

export default PostBox;
