import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';

Modal.setAppElement('#root');

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([
    { id: 1, content: 'Just started a new project! Excited to share the progress.' },
    { id: 2, content: 'Attended a fantastic workshop today. Learned so much!' },
    { id: 3, content: 'Feeling motivated to code more today!' },
  ]);
  const [newPost, setNewPost] = useState('');
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [cropper, setCropper] = useState(null);
  const [imageType, setImageType] = useState(''); // 'cover' or 'profile'

  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    if (user && token) {
      fetchProfile();
    }
  }, [user, token]);

  const handleFileChange = (event, type) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setModalImage(reader.result);
        setImageType(type);
        setModalIsOpen(true);
      };
      reader.readAsDataURL(file);
    } else {
      console.error('No file selected.');
    }
  };

  const handleImageUpload = async () => {
    if (!cropper) return;

    const canvas = cropper.getCroppedCanvas();
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      const type = imageType;
      formData.append(type === 'cover' ? 'cover_pic' : 'profile_pic', blob, 'cropped-image.jpg');

      try {
        await axios.patch(`${BASE_URL}/api/users/profile/update/`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        });

        if (type === 'cover') {
          setProfile((prevProfile) => ({
            ...prevProfile,
            cover_pic: URL.createObjectURL(blob),
          }));
        } else {
          setProfile((prevProfile) => ({
            ...prevProfile,
            profile_pic: URL.createObjectURL(blob),
          }));
        }
        closeModal();
      } catch (err) {
        setError('An error occurred while uploading the image.');
      }
    }, 'image/jpeg');
  };

  const handlePostSubmit = async () => {
    if (!newPost.trim()) {
      setError('Post content cannot be empty.');
      return;
    }

    try {
      // Simulate posting to the server
      setPosts((prevPosts) => [
        ...prevPosts,
        { id: Date.now(), content: newPost },
      ]);
      setNewPost(''); // Clear the input field
    } catch (err) {
      setError('An error occurred while submitting the post.');
    }
  };

  const openModal = (type) => {
    setImageType(type);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalImage('');
  };

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return <div className="text-center mt-4">Loading...</div>;
  }

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      {/* Cover Photo */}
      <div
        className="relative h-60 bg-cover bg-center cursor-pointer"
        style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x500'})` }}
        onClick={() => openModal('cover')}
      >
        <label htmlFor="cover-upload" className="absolute top-4 right-4 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100">
          <FaCamera className="text-gray-600 text-lg" />
        </label>
        <input id="cover-upload" type="file" onChange={(e) => handleFileChange(e, 'cover')} className="hidden" />
        <button
          onClick={handleImageUpload}
          className="absolute top-4 right-12 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
        >
          Upload
        </button>

        {/* Profile Picture */}
        <div className="absolute bottom-0 left-4 transform -translate-y-1/2">
          <img
            src={profile.profile_pic || 'https://via.placeholder.com/100'}
            alt="Profile"
            className="w-24 h-24 rounded-full border-4 border-white shadow-md cursor-pointer"
            onClick={() => openModal('profile')}
          />
          <label htmlFor="profile-upload" className="absolute bottom-2 left-12 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100">
            <FaCamera className="text-gray-600 text-lg" />
          </label>
          <input id="profile-upload" type="file" onChange={(e) => handleFileChange(e, 'profile')} className="hidden" />
          <button
            onClick={handleImageUpload}
            className="absolute bottom-2 left-24 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
          >
            Upload
          </button>
        </div>
      </div>

      {/* Profile Details */}
      <div className="p-6">
        <div className="flex justify-between items-center mb-4">
          {/* First Name */}
          <div>
            <h2 className="text-2xl font-semibold">{profile.first_name}</h2>
            {/* Username Under First Name */}
            <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{`@${profile.username}`}</h2>
          </div>

          {/* Edit Profile Button */}
          <Link
            to="/edit-profile"
            className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white"
          >
            Edit Profile
          </Link>
        </div>

        <p className="text-gray-600">{profile.bio || 'No bio available'}</p>

        {/* Post Box */}
        <div className="mt-6">
          <textarea
            className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500"
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
          />
          <button
            onClick={handlePostSubmit}
            className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-md shadow-lg hover:bg-blue-600"
          >
            Post
          </button>
        </div>

        {/* Feed Section */}
        <div className="mt-8">
          <h3 className="text-xl font-semibold mb-4">Your Posts</h3>
          {posts.length > 0 ? (
            posts.map((post) => (
              <div key={post.id} className="border p-4 mb-4 rounded-md shadow-sm">
                {post.content}
              </div>
            ))
          ) : (
            <p className="text-gray-500">You haven't posted anything yet.</p>
          )}
        </div>
      </div>

      {/* Image Modal */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 p-4 z-50"
        overlayClassName="fixed inset-0 bg-black bg-opacity-75 z-40"
      >
        <div className="relative">
          <Cropper
            src={modalImage}
            style={{ height: 400, width: '100%' }}
            aspectRatio={1}
            guides={false}
            crop={(event) => setCropper(event.target)}
            ref={(c) => setCropper(c)}
          />
          <button
            onClick={handleImageUpload}
            className="absolute bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
          >
            Upload
          </button>
          <button
            onClick={closeModal}
            className="absolute bottom-4 left-4 bg-red-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-red-600"
          >
            Close
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default ProfilePage;
