import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';
import { updateUserProfileImage } from '../features/auth/userSlice';
import { showErrorToast } from './ErroToast';

Modal.setAppElement('#root');

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState('');
  const [newPostFile, setNewPostFile] = useState(null); // File upload for post
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [imageType, setImageType] = useState(''); // 'cover' or 'profile'
  const cropperRef = useRef(null);

  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/posts/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setPosts(response.data);
      } catch (err) {
        setError('Failed to fetch posts.');
      }
    };

    if (user && token) {
      fetchProfile();
      fetchPosts();
    }
  }, [user, token]);

  // Function to handle file change for cover or profile
  const handleFileChange = (e, type) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setModalImage(reader.result);
        setImageType(type);
        setModalIsOpen(true); // Open the modal for cropping
      };
      reader.readAsDataURL(file);
    }
  };

  // Function to crop the image and upload it
  const cropImage = () => {
    if (cropperRef.current) {
      const croppedImageDataUrl = cropperRef.current.cropper.getCroppedCanvas().toDataURL();
      if (imageType === 'cover') {
        setCoverImage(croppedImageDataUrl);
      } else if (imageType === 'profile') {
        setProfileImage(croppedImageDataUrl);
      }
      setModalIsOpen(false); // Close the modal after cropping
    }
  };

  const handlePostFileChange = (e) => {
    setNewPostFile(e.target.files[0]);
  };

  const handlePostSubmit = async () => {
    if (!newPost.trim() && !newPostFile) {
      setError('Post content or file must not be empty.');
      return;
    }

    const formData = new FormData();
    formData.append('content', newPost);
    if (newPostFile) {
      formData.append('file', newPostFile);
    }

    try {
      const response = await axios.post(`${BASE_URL}/api/posts/create/`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setPosts((prevPosts) => [response.data, ...prevPosts]);
      setNewPost('');
      setNewPostFile(null);
    } catch (err) {
      setError('An error occurred while submitting the post.');
    }
  };

  const openModal = (type) => {
    setImageType(type);
    document.getElementById(`${type}-upload`).click();
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalImage('');
  };

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return <div className="text-center mt-4">Loading...</div>;
  }

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      {/* Cover Photo */}
      <div
        className="relative h-60 bg-cover bg-center cursor-pointer"
        style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x500'})` }}
      >
        <label
          htmlFor="cover-upload"
          className="absolute top-4 right-4 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
        >
          <FaCamera className="text-gray-600 text-lg" />
        </label>
        <input
          id="cover-upload"
          type="file"
          accept="image/jpeg,image/jpg,image/png"
          onChange={(e) => handleFileChange(e, 'cover')}
          className="hidden"
        />
        <button
          onClick={() => openModal('cover')}
          className="absolute top-4 right-12 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
        >
          Upload
        </button>

        {/* Profile Picture */}
        <div className="absolute bottom-0 left-4 transform -translate-y-1/2">
          <div className="relative">
            <img
              src={profile.profile_pic || 'https://via.placeholder.com/100'}
              alt="Profile"
              className="w-24 h-24 rounded-full border-4 border-white shadow-md cursor-pointer object-cover"
              onClick={() => openModal('profile')}
            />
            <label
              htmlFor="profile-upload"
              className="absolute bottom-2 left-12 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
            >
              <FaCamera className="text-gray-600 text-lg" />
            </label>
            <input
              id="profile-upload"
              type="file"
              onChange={(e) => handleFileChange(e, 'profile')}
              className="hidden"
            />
            <button
              onClick={() => openModal('profile')}
              className="absolute bottom-2 left-24 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
            >
              Upload
            </button>
          </div>
        </div>
      </div>

      {/* Profile Details */}
      <div className="p-6">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h2 className="text-2xl font-semibold">{profile.first_name}</h2>
            <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{`@${profile.username}`}</h2>
          </div>

          <div className="flex space-x-4">
            <Link
              to="/profile-detail"
              className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white"
            >
              Profile
            </Link>
            <Link
              to="/edit-profile"
              className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white"
            >
              Edit Profile
            </Link>
          </div>
        </div>

        <p className="text-gray-600 mb-6">{profile.bio || 'No bio available'}</p>

        {/* Post Box */}
        <div className="mb-6">
          <textarea
            className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500"
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
          />
          <input
            type="file"
            accept="image/*,video/*"
            onChange={handlePostFileChange}
            className="mt-2"
          />
          <button
            onClick={handlePostSubmit}
            className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
          >
            Post
          </button>
        </div>

        {/* Posts */}
        <div>
          {posts.map((post) => (
            <div key={post.id} className="mb-4 p-4 border border-gray-200 rounded-md shadow-sm">
              <p>{post.content}</p>
              {post.file && <img src={post.file} alt="post" className="mt-2 max-h-60 object-contain" />}
              <p className="text-gray-500 text-sm mt-2">{new Date(post.created_at).toLocaleString()}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Modal for Cropping */}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        <div className="flex justify-center items-center">
          <Cropper
            ref={cropperRef}
            src={modalImage}
            style={{ height: 400, width: '100%' }}
            aspectRatio={imageType === 'cover' ? 16 / 9 : 1 / 1}
            viewMode={1}
            guides={false}
          />
        </div>
        <div className="flex justify-center mt-4">
          <button onClick={cropImage} className="bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600">
            Save
          </button>
          <button onClick={closeModal} className="ml-4 bg-gray-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-gray-600">
            Cancel
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default ProfilePage;
