import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BASE_URL } from '../config';
import { useSelector } from 'react-redux';
import { Link, useParams } from 'react-router-dom';

const Following = () => {

  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);
  const { userId } = useParams(); 
  const [following, setFollowing] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch following list when component loads
    fetchFollowing();
  }, [token,user]);
  const checkUser=user.id

  const fetchFollowing = async () => {
    setLoading(true);
    try {
        const response = await axios.get(`${BASE_URL}/api/interactions/connections/?user_id=${userId}&type=following`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          setFollowing(response.data);
    } catch (error) {
      console.error('Error fetching following:', error);
    }
    finally {
        setLoading(false); 
      }
  };

  const handleUnfollow = async (userId) => {
    try {
        await axios.post(`${BASE_URL}/api/interactions/unfollow/${userId}/`, { follower_id: user.id }, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
      // Update the following list after unfollowing
      setFollowing(following.filter(follow => follow.id !== userId));
    } catch (error) {
      console.error('Error unfollowing user:', error);
    }
  };

  const filteredFollowing = following.filter(user =>
    user.username.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="max-w-lg mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Following</h1>
      <input
        type="text"
        placeholder="Search following..."
        className="mb-4 p-2 w-full border border-gray-300 rounded-md"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {loading ? (
        <div className="flex justify-center items-center h-48">
          <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
        </div>
      ) : filteredFollowing.length === 0 ? (
        <div className="text-center text-gray-500">Not following anyone yet.</div>
      ) : (
        <ul className="space-y-4">
          {filteredFollowing.map((user) => (
            <Link
            to={`/profileview/${user.id}`}
            className="text-blue-500 hover:text-blue-700"
            title="Profile"
          >
            <li key={user.id} className="flex items-center justify-between bg-white p-4 rounded-lg shadow-md">
              <div className="flex items-center space-x-4">
                <img src={user.profile_pic} alt="Profile" className="w-10 h-10 rounded-full object-cover" />
                <span>{user.username}</span>
              </div>
              { checkUser == userId ? (
              <button
                onClick={() => handleUnfollow(user.id)}
                className="text-blue-500 border border-blue-500 px-4 py-1 rounded-md hover:bg-blue-500 hover:text-white"
              >
                Unfollow
              </button> ) :null}
            </li>
            </Link>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Following;
