import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import CommentItem from '../components/CommentItem';
import { showErrorToast } from '../components/ErroToast';
import { showSuccessToast } from '../components/CustomToast';


const UserProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [likedPosts, setLikedPosts] = useState({});
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState('');
  const [openCommentId, setOpenCommentId] = useState(null);

  const { id } = useParams(); // Get userId from URL params
  const token = useSelector((state) => state.auth.token);

  useEffect(() => {
    console.log('hi all')
    console.log(id)
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        showErrorToast('Error fetching profile');
      }
    };

    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/posts/?user_id=${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setPosts(response.data);
      } catch (err) {
        showErrorToast('Error fetching posts');
      }
    };

    if (id && token) {
      fetchProfile();
      fetchPosts();
    }
  }, [id, token]);

  const fetchComments = async (postId) => {
    try {
      const response = await axios.get(`${BASE_URL}/api/posts/${postId}/comments/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setComments((prev) => ({
        ...prev,
        [postId]: response.data,
      }));
    } catch (error) {
      showErrorToast('Error fetching comments');
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await fetch(`${BASE_URL}/api/likes/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ post: postId }),
      });

      if (response.status === 201) {
        setLikedPosts((prev) => ({ ...prev, [postId]: true }));
        showSuccessToast('Post liked');
      } else if (response.status === 204) {
        setLikedPosts((prev) => ({ ...prev, [postId]: false }));
        showSuccessToast('Post unliked');
      }
    } catch (error) {
      showErrorToast('Error liking/unliking post');
    }
  };

  const handleToggleComments = (postId) => {
    if (openCommentId === postId) {
      setOpenCommentId(null);
    } else {
      setOpenCommentId(postId);
      fetchComments(postId);
    }
  };

  const handleSubmitComment = async (postId, commentText) => {
    if (!commentText.trim()) {
      showErrorToast('Empty comment box');
      return;
    }

    try {
      const response = await axios.post(
        `${BASE_URL}/api/comments/`,
        {
          post: postId,
          content: commentText,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setComments((prev) => ({
        ...prev,
        [postId]: [...(prev[postId] || []), response.data],
      }));
      setNewComment('');
      showSuccessToast('Comment submitted');
    } catch (error) {
      showErrorToast('Error submitting comment');
    }
  };

  if (!profile) return <div>Loading...</div>;

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
        <div
    className="relative h-48 sm:h-60 bg-cover bg-center cursor-pointer"
    style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x500'})` }}
     ></div>
       <div className="absolute bottom-0 left-2 sm:left-4 transform -translate-y-1/2">
      <div className="relative">
        <img
          src={profile.profile_pic || 'https://via.placeholder.com/100'}
          alt="Profile"
          accept="image/*"
          className="w-20 h-20 sm:w-24 sm:h-24 rounded-full border-4 border-white shadow-md cursor-pointer object-cover"
         
        />
        <label
          htmlFor="profile-upload"
          className="absolute bottom-1 left-10 sm:left-12 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
        >
          <FaCamera className="text-gray-600 text-lg" />
        </label>
       
       
      </div>
    </div>
      {/* Display User's Profile Info */}
      <div className="p-4">
        <h2 className="text-2xl font-bold">{profile.username}</h2>
        <p>{profile.bio}</p>
      </div>

      {/* Display User's Posts */}
      <div className="p-4">
        {posts.map((post) => (
          <div key={post.id} className="mb-4">
            <p>{post.content}</p>

            {/* Like Button */}
            <button onClick={() => handleLike(post.id)}>
              {likedPosts[post.id] ? 'Unlike' : 'Like'}
            </button>

            {/* Comments Section */}
            <button onClick={() => handleToggleComments(post.id)}>
              {openCommentId === post.id ? 'Hide Comments' : 'Show Comments'}
            </button>

            {openCommentId === post.id && (
              <div>
                {comments[post.id]?.map((comment) => (
                  <CommentItem key={comment.id} comment={comment} />
                ))}

                {/* Comment Input */}
                <input
                  type="text"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="Write a comment"
                />
                <button onClick={() => handleSubmitComment(post.id, newComment)}>
                  Comment
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserProfilePage;
