import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';
import { BASE_URL } from '../../config';

// Async thunk to fetch profile data
export const fetchProfile = createAsyncThunk(
  'profile/fetchProfile',
  async ({ userId, token }, { rejectWithValue }) => {
    try {
      const response = await axios.get(`${BASE_URL}/api/users/profile/${userId}/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return response.data;
    } catch (error) {
      if (error.response.status === 401) {
        // Handle unauthorized error
        return rejectWithValue('Session expired. Please log in again.');
      }
      return rejectWithValue(error.response?.data?.detail || 'Failed to fetch profile.');
    }
  }
);

// Async thunk to fetch posts
export const fetchPosts = createAsyncThunk(
  'profile/fetchPosts',
  async ({ userId, token }, { rejectWithValue }) => {
    try {
      const response = await axios.get(`${BASE_URL}/api/posts/?user_id=${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return response.data;
    } catch (error) {
      return rejectWithValue('Failed to fetch posts.');
    }
  }
);

// Async thunk to fetch bookmarks
export const fetchBookmarks = createAsyncThunk(
  'profile/fetchBookmarks',
  async (token, { rejectWithValue }) => {
    try {
      const response = await axios.get(`${BASE_URL}/api/bookmarks`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return response.data.map((bookmark) => bookmark.post);
    } catch (error) {
      return rejectWithValue('Failed to fetch bookmarks.');
    }
  }
);

// Slice
const profileSlice = createSlice({
  name: 'profile',
  initialState: {
    profile: null,
    posts: [],
    bookmarks: [],
    likedPosts: {},
    comments: {},
    loading: false,
    error: null,
  },
  reducers: {
    addComment(state, action) {
      const { postId, comment } = action.payload;
      state.comments[postId] = [...(state.comments[postId] || []), comment];
    },
    updateLikeStatus(state, action) {
      const { postId, liked } = action.payload;
      state.likedPosts[postId] = liked;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchProfile.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchProfile.fulfilled, (state, action) => {
        state.profile = action.payload;
        state.loading = false;
      })
      .addCase(fetchProfile.rejected, (state, action) => {
        state.error = action.payload;
        state.loading = false;
      })
      .addCase(fetchPosts.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchPosts.fulfilled, (state, action) => {
        state.posts = action.payload;
        state.loading = false;
      })
      .addCase(fetchPosts.rejected, (state, action) => {
        state.error = action.payload;
        state.loading = false;
      })
      .addCase(fetchBookmarks.fulfilled, (state, action) => {
        state.bookmarks = action.payload;
      })
      .addCase(fetchBookmarks.rejected, (state, action) => {
        state.error = action.payload;
      });
  },
});

export const { addComment, updateLikeStatus } = profileSlice.actions;
export const profileReducer  =profileSlice.reducer;
