// src/components/Login.js
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { loginSuccess } from '../features/auth/authSlice';
import logo from '../assets/logo.png';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { BASE_URL } from '../config';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
        const response = await axios.post(`${BASE_URL}/api/users/login/`, {
        email: email,
        password: password,
      });
      const { token, is_admin } = response.data;
      dispatch(loginSuccess({ user: { email }, token }))
      navigate('/');
      console.log('Login successful:', response.data);
    } catch (error) {
        console.error('Error logging in:', error.response?.data || error.message);
    }
 
  }

  return ( 
  <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-400 to-indigo-600">
    
    <form
      onSubmit={handleSubmit}
      className="flex flex-col gap-6 p-8 bg-white rounded-lg shadow-lg w-full max-w-md"
    >
         <div className="flex flex-col items-center mb-2">
      {/* Logo Image */}
      <img
        src={logo} // Replace with your image path or URL
        alt="Logo"
        className="w-20 h-20 mb-1"
      />
      {/* App Name */}
      <h1 className="text-3xl font-semibold text-gray-800">Connectify</h1>
    </div>
      <h2 className="text-2xl font-bold text-center text-gray-800">Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="p-3 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
      />
      <button
        type="submit"
        className="py-3 text-white bg-blue-500 rounded-lg hover:bg-blue-600 transition duration-200 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
      >
        Login
      </button>
      <div className="flex justify-between text-sm text-gray-600">
        <a href="#" className="hover:text-blue-500">Forgot password?</a>
        <a href="#" className="hover:text-blue-500">Sign up</a>
      </div>
    </form>
  </div>
  );
};

export default Login;
