import React, { useState } from 'react';
import { FaSmile, FaImage, FaVideo } from 'react-icons/fa';

const PostBox = ({ onPostSubmit }) => {
  const [postContent, setPostContent] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [error, setError] = useState(null);

  const handleContentChange = (e) => {
    setPostContent(e.target.value);
  };

  const handleFileChange = (event, type) => {
    const file = event.target.files[0];
    if (!file) return;

    const validImageTypes = ['image/jpeg', 'image/png', 'image/jpg'];
    const validVideoTypes = ['video/mp4', 'video/webm', 'video/ogg'];

    if (type === 'image' && validImageTypes.includes(file.type)) {
      setSelectedImage(URL.createObjectURL(file));
      setSelectedVideo(null); // Clear video if image is selected
    } else if (type === 'video' && validVideoTypes.includes(file.type)) {
      setSelectedVideo(URL.createObjectURL(file));
      setSelectedImage(null); // Clear image if video is selected
    } else {
      setError('Please select a valid image or video file.');
    }
  };

  const handlePost = () => {
    if (!postContent.trim() && !selectedImage && !selectedVideo) {
      setError('Post content or media is required.');
      return;
    }

    onPostSubmit({ content: postContent, image: selectedImage, video: selectedVideo });
    setPostContent('');
    setSelectedImage(null);
    setSelectedVideo(null);
    setError(null);
  };

  return (
    <div className="border p-4 rounded-md shadow-sm">
      <textarea
        className="w-full p-2 border border-gray-300 rounded-md mb-2"
        placeholder="What's on your mind?"
        value={postContent}
        onChange={handleContentChange}
      />
      
      <div className="flex items-center space-x-4 mb-4">
        <button type="button" className="flex items-center space-x-1">
          <FaSmile className="text-xl text-gray-500" />
        </button>

        <label htmlFor="image-upload" className="cursor-pointer flex items-center space-x-1">
          <FaImage className="text-xl text-gray-500" />
          <input
            id="image-upload"
            type="file"
            accept="image/*"
            onChange={(e) => handleFileChange(e, 'image')}
            className="hidden"
          />
        </label>

        <label htmlFor="video-upload" className="cursor-pointer flex items-center space-x-1">
          <FaVideo className="text-xl text-gray-500" />
          <input
            id="video-upload"
            type="file"
            accept="video/*"
            onChange={(e) => handleFileChange(e, 'video')}
            className="hidden"
          />
        </label>
      </div>

      {selectedImage && (
        <div className="mb-2">
          <img src={selectedImage} alt="Selected" className="max-w-full h-auto rounded-md" />
        </div>
      )}

      {selectedVideo && (
        <div className="mb-2">
          <video controls src={selectedVideo} className="max-w-full h-auto rounded-md" />
        </div>
      )}

      {error && <p className="text-red-500 text-sm">{error}</p>}

      <button
        className="bg-blue-500 text-white px-4 py-2 rounded-md shadow hover:bg-blue-600"
        onClick={handlePost}
      >
        Post
      </button>
    </div>
  );
};

export default PostBox;
