import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const user = useSelector((state) => state.user.user); // Access user from Redux store
  const token = useSelector((state) => state.auth.token); // Access token from Redux store

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    if (user && token) {
      fetchProfile();
    }
  }, [user, token]);

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return <div className="text-center mt-4">Loading...</div>;
  }

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      {/* Cover Photo */}
      <div className="relative h-40 bg-cover bg-center" style={{ backgroundImage: `url(${profile.background_image || 'https://via.placeholder.com/800x200'})` }}>
        {/* Profile Picture */}
        <div className="absolute bottom-0 left-4 transform -translate-y-1/2">
          <img
            src={profile.profile_pic || 'https://via.placeholder.com/100'}
            alt="Profile"
            className="w-20 h-20 rounded-full border-4 border-white"
          />
        </div>
      </div>

      {/* Profile Details */}
      <div className="p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">{profile.username}</h2>
          <button className="text-blue-500 border border-blue-500 px-4 py-1 rounded-full hover:bg-blue-500 hover:text-white">
            Edit Profile
          </button>
        </div>
        <p className="text-gray-600">{profile.bio || 'No bio available'}</p>

        {/* Post Box */}
        <div className="mt-6">
          <textarea
            className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:border-blue-500"
            placeholder="What's on your mind?"
          />
          <button className="mt-2 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600">
            Post
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
