import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage } from '../features/auth/chatSlice';

const ChatDialog = ({ roomName, user }) => {
    console.log(`roomName: ${roomName}`);
    const dispatch = useDispatch();
    const token = useSelector((state) => state.auth.token);
    const messages = useSelector((state) => state.chat.messages);
    const [messageInput, setMessageInput] = useState('');
    const socketRef = useRef(null); // Using useRef for socket

    useEffect(() => {
        // Connect to WebSocket
        socketRef.current = new WebSocket(`ws://localhost:8000/ws/chat/${roomName}/?token=${token}`);

        socketRef.current.onmessage = (e) => {
            const data = JSON.parse(e.data);
            if (data.type === 'history') {
                data.messages.forEach((msg) => {
                    dispatch(addMessage(msg)); // Dispatching addMessage action to store messages in Redux
                });
            } else if (data.type === 'message') {
                dispatch(addMessage(data)); // Dispatching addMessage action for new messages
            }
        };

        socketRef.current.onopen = () => {
            console.log('WebSocket connection opened.');
            socketRef.current.send(JSON.stringify({ token })); // Send the token if needed
        };

        socketRef.current.onerror = (error) => {
            console.error('WebSocket error:', error);
        };

        socketRef.current.onclose = () => {
            console.log('WebSocket connection closed.');
        };

        return () => {
            socketRef.current.close();
        };
    }, [roomName, dispatch, token]); // Include token in dependency array

    const sendMessage = () => {
        if (messageInput && socketRef.current.readyState === WebSocket.OPEN) {
            socketRef.current.send(JSON.stringify({
                message: messageInput,
                user_id: user.id,
            }));
            setMessageInput('');
        } else if (!messageInput) {
            console.log('Message input is empty.');
        }
    };

    return (
        <div className="bg-white shadow-md rounded-lg p-4 h-full">
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index}>
                        <strong>{msg.sender}:</strong> {msg.content}
                        <span>{new Date(msg.timestamp).toLocaleTimeString()}</span>
                    </div>
                ))}
            </div>
            <input
                type="text"
                value={messageInput}
                onChange={(e) => setMessageInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default ChatDialog;
