import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import CommentItem from '../components/CommentItem';
import { showErrorToast } from '../components/ErroToast';
import { showSuccessToast } from '../components/CustomToast';
import { FaCamera } from 'react-icons/fa';
import { FaSmile, FaTimes } from 'react-icons/fa';
import Picker from '@emoji-mart/react';
import data from '@emoji-mart/data';
import { logout } from '../features/auth/userSlice';
import PostListComponent from '../components/ProfilePageComponents/PostListComponent';

const UserProfilePage = () => {
  //const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [likedPosts, setLikedPosts] = useState({});
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState('');
  const [openCommentId, setOpenCommentId] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [commentText, setCommentText] = useState('');
  const [replyingToCommentId, setReplyingToCommentId] = useState(null); 
  const { id } = useParams(); // Get userId from URL params
  const token = useSelector((state) => state.auth.token);
  const user = useSelector((state) => state.user.user);
  //const [isFollowing, setIsFollowing] = useState(false); 
  //const [followerCount, setFollowerCount] = useState(0); 
  //const [followingCount, setFollowingCount] = useState(0);
  const [bookmarks, setBookmarks] = useState([]);
  const [bookmarkPosts, setbookmarkPosts] = useState({});  
  const dispatch = useDispatch()
  const navigate=useNavigate();
  const { profile, isFollowing, followerCount, followingCount, loading, error } = useSelector((state) => state.profile);
  useEffect(() => {
  
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
        console.log(response.data)
        setFollowerCount(response.data.followers);
        setFollowingCount(response.data.following);
        setIsFollowing(response.data.is_following);
      } catch (err) {
        if (err.response?.status === 401) {
          showErrorToast("Session timed out!.Please login agian.")
          setTimeout(() => {
            handleLogout(); 
          }, 1000);
         
        } else {
          
          showErrorToast('An error occurred while fetching the profile.');
        }
      }
    };


    if (id && token) {
      fetchProfile();
    }
  }, [id, token]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/api/posts/?user_id=${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPosts(response.data);
    } catch (err) {
      showErrorToast('Error fetching posts');
    }
  };

  const fetchBookmarks = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/api/bookmarks`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
      });  // Fetch user's bookmarks
      setBookmarks(response.data.map(bookmark => bookmark.post));
    } catch (error) {
      console.error('Error fetching bookmarks:', error);
    }
  };

  const fetchComments = async (postId) => {
    try {
      console.log(`calling feetchcomments:${postId}`)
      const response = await axios.get(`${BASE_URL}/api/posts/${postId}/comments/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log(`getting comment resposne:${response.data}`)
      setComments((prevComments) => ({
        ...prevComments,
        [postId]: response.data,
        
      }));
    } catch (error) {
      console.log("Error fetching comments");
    }
  };
  useEffect(() => {
    if (id && token) {
      fetchPosts();
      fetchBookmarks();
    }
  }, [id, token]);

  useEffect(() => {
    if (posts.length > 0) {
      posts.forEach((post) => {
        console.log(`Correct post id: ${post.id}`);
        fetchComments(post.id);
      });
    }
  }, [posts]);

  useEffect(() => {
    const fetchLikeStatus = async () => {
      try {
        const likeStatuses = {};
        for (let post of posts) {
          const response = await fetch(`${BASE_URL}/api/posts/${post.id}/check_liked/`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${token}`,
            },
          });

          if (response.ok) {
            const result = await response.json();
            likeStatuses[post.id] = result.liked; // Assume the response contains { liked: true/false }
            console.log('check liked');
            console.log(result);
          }
        }
        setLikedPosts(likeStatuses);
      } catch (error) {
        console.error('Error fetching like status:', error);
      }
    };
    const fetchBookmarkStatus = async () => {
      try {
        const bookmarkPosts = {};
        for (let post of posts) {
          const response = await fetch(`${BASE_URL}/api/posts/${post.id}/check_bookmarked/`, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${token}`,
            },
          });

          if (response.ok) {
            const result = await response.json();
            bookmarkPosts[post.id] = result.bookmarked; // Assume the response contains { liked: true/false }
           
          }
        }
        setbookmarkPosts(bookmarkPosts);
      } catch (error) {
        console.error('Error fetching like status:', error);
      }
    };
    
    fetchLikeStatus();
    fetchBookmarkStatus();
  }, [posts, token]);

  const handleLogout=()=>{
    dispatch(logout());
    navigate('/login')
  }

  const handleLike = async (postId) => {
    try {
      const response = await fetch(`${BASE_URL}/api/likes/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ post: postId }),
      });

      if (response.status === 201) {
        setLikedPosts((prev) => ({ ...prev, [postId]: true }));
        showSuccessToast('Post liked');
      } else if (response.status === 204) {
        setLikedPosts((prev) => ({ ...prev, [postId]: false }));
        showSuccessToast('Post unliked');
      }
      fetchPosts();
    } catch (error) {
      showErrorToast('Error liking/unliking post');
    }
  };

  const handleToggleComments = (postId) => {
    if (openCommentId === postId) {
      setOpenCommentId(null);
    } else {
      setOpenCommentId(postId);
      fetchComments(postId);
    }
  };
  const handleSubmitComment = async (postId, commentText, parentId = null) => {
    if (!commentText.trim()) {
      showErrorToast('Empty Comment Box');
      return;
    }
    console.log(postId,commentText,user.id,parentId,token);
    try {
      const response = await axios.post(`${BASE_URL}/api/comments/`, {
        post: postId,
        content: commentText,
        user: user.id,
        parent: parentId, // Include parentId here
      }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
  
      const newCommentData = response.data;
      setComments((prevComments) => ({
        ...prevComments,
        [postId]: [...(prevComments[postId] || []), newCommentData],
      }));
  
      setNewComment("");
      setReplyingToCommentId(null);
     
      showSuccessToast("Comment submitted successfully");
      fetchPosts();
    } catch (error) {
      showErrorToast("Error submitting comment");
    }
  };



  const handleAddEmoji = (emoji) => {
    setCommentText((prev) => prev + emoji.native); // Append the selected emoji to the comment text
  };
  const handleFollowUnfollow = async  () => {
    try {
      if (!isFollowing) {
        await axios.post(`${BASE_URL}/api/interactions/follow/${id}/`, { follower_id: user.id }, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setFollowerCount((prev) => prev + 1);
        showSuccessToast('Followed successfully');
      } else {
        await axios.post(`${BASE_URL}/api/interactions/unfollow/${id}/`, { follower_id: user.id }, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setFollowerCount((prev) => prev - 1);
        showSuccessToast('Unfollowed successfully');
      }
      setIsFollowing((prev) => !prev); // Toggle following status
    } catch (error) {
      showErrorToast('Error in follow/unfollow action');
    }
  };

  const handleBookmark = async (postId) => {
    try {
      
      if (bookmarkPosts[postId]) {
        // If already bookmarked, unbookmark the post
        await axios.delete(`${BASE_URL}/api/bookmarks/${postId}`,{
          headers: {
              Authorization: `Bearer ${token}`,
          },
        });
        setbookmarkPosts((prevState) => ({
          ...prevState,
          [postId]: false,
        }));
        showErrorToast('Post removed from Bookmarks');
      } else {
        // If not bookmarked, bookmark the post
        await axios.post(`${BASE_URL}/api/bookmarks/`, { post: postId }, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setbookmarkPosts((prevState) => ({
          ...prevState,
          [postId]: true,
        }));
        showSuccessToast('Added to Bookmarks');
      }
     
    } catch (error) {
      console.error('Error updating bookmark:', error);
    }
  };

  if (!profile) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
      </div>
    );
  }
  

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
        <div
    className="relative h-48 sm:h-60 bg-cover bg-center cursor-pointer"
    style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x500'})` }}
  >
   
   
    

    {/* Profile Picture */}
    <div className="absolute bottom-0 left-2 sm:left-4 transform -translate-y-1/2">
      <div className="relative">
        <img
          src={profile.profile_pic || 'https://via.placeholder.com/100'}
          alt="Profile"
          accept="image/*"
          className="w-20 h-20 sm:w-24 sm:h-24 rounded-full border-4 border-white shadow-md cursor-pointer object-cover"
          
        />
    
        
        
            </div>
            </div>
        </div>
        <div className="p-4 sm:p-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4">
              {/* User Info */}
              <div className="text-center sm:text-left flex items-center space-x-4 mb-4 sm:mb-0">
                <div>
                  <h2 className="text-xl sm:text-2xl font-semibold">{profile.first_name}</h2>
                  <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{`@${profile.username}`}</h2>
                </div>
              </div>

              {/* Metrics and Button Section */}
              <div className="flex flex-col sm:flex-row sm:items-center sm:space-x-6">
              <div className="flex flex-col items-center">
              {isFollowing ? (
                <Link to={`/followers/${profile.id}`}>
                  <p className="text-lg font-semibold cursor-pointer">{followerCount}</p>
                </Link>
              ) : (
                <p className="text-lg font-semibold cursor-not-allowed">{followerCount}</p>
              )}
              <p className="text-sm text-gray-600 font-semibold">Followers</p>
             
            </div>

          
            <div className="flex flex-col items-center">
            {isFollowing ? (
              <Link to={`/following/${profile.id}`}>
                <p className="text-lg font-semibold cursor-pointer">{followingCount}</p>
              </Link>
              ):(
                <p className="text-lg font-semibold cursor-pointer">{followingCount}</p>
              )}
              <p className="text-sm text-gray-600 font-semibold">Following</p>
            </div>

                <div className="flex justify-center">
                  <button
                    onClick={handleFollowUnfollow}
                    className={`px-4 py-2 rounded-lg font-semibold text-white ${
                      isFollowing ? 'bg-red-500' : 'bg-blue-500'
                    } hover:opacity-90`}
                  >
                    {isFollowing ? 'Unfollow' : 'Follow'}
                  </button>
                </div>
              </div>
            </div>

        
            <PostListComponent 
              posts={posts}
              user={user}
              likedPosts={likedPosts}
              handleLike={handleLike}
              handleToggleComments={handleToggleComments}
              handleBookmark={handleBookmark}
              bookmarkPosts={bookmarkPosts}
              openCommentId={openCommentId}
              handleSubmitComment={handleSubmitComment}
              replyingToCommentId={replyingToCommentId}
              setReplyingToCommentId={setReplyingToCommentId}
              setCommentText={setCommentText}
              setShowEmojiPicker={setShowEmojiPicker}
              showEmojiPicker={showEmojiPicker}
              handleAddEmoji={handleAddEmoji}
              comments={comments}
              commentText={commentText}
              />
       
           
            
        </div>
     

      
    </div>
  );
};

export default UserProfilePage;
