import React, { useEffect, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { BASE_URL } from '../config';
import { FaCamera } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import Cropper from 'react-cropper';
import 'cropperjs/dist/cropper.css';
import { updateUserProfileImage } from '../features/auth/userSlice';
import { showErrorToast } from '../components/ErroToast';
import ImageCropper from '../components/Cropper';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import PostBox from '../components/PostBoxComponent';
import { showSuccessToast } from '../components/CustomToast';

Modal.setAppElement('#root');

const ProfilePage = () => {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([
    { id: 1, content: 'Just started a new project! Excited to share the progress.' },
    { id: 2, content: 'Attended a fantastic workshop today. Learned so much!' },
    { id: 3, content: 'Feeling motivated to code more today!' },
  ]);
  const [newPost, setNewPost] = useState('');
  const [coverImage, setCoverImage] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [imageType, setImageType] = useState(''); // 'cover' or 'profile'
  const cropperRef = useRef(null);

  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);
  const dispatch = useDispatch()
  console.log(user.id)
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/users/profile/${user?.id}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.detail || 'An error occurred while fetching the profile.');
      }
    };

    if (user && token) {
      fetchProfile();
    }
  }, [user, token]);
  
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get(`${BASE_URL}/api/posts/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setPosts(response.data); 
        console.log(response.data)// Set the fetched posts
      } catch (err) {
        showErrorToast("Error fetching posts");
      }
    };

    if (user && token) {
      fetchPosts();
    }
  }, [user, token]);


  const handleFileChange = (event, type) => {
    const fileInput = event.target;
    const file = event.target.files[0];
    if (file) {
      const validImageTypes = ['image/jpeg', 'image/jpg', 'image/png'];
      if (!validImageTypes.includes(file.type)) {
        // setError('Please select a valid image file (JPEG, JPG, or PNG).');
        showErrorToast('Please select a valid image file (JPEG, JPG, or PNG).');
        fileInput.value = null; 
        return;
      }
    }
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setModalImage(reader.result);
        setImageType(type);
        setModalIsOpen(true);
        fileInput.value = null; 
      };
      reader.readAsDataURL(file);
    } else {
      console.error('No file selected.');
    }
  };

  const handleImageUpload = async () => {
    if (!cropperRef.current) return;

    const cropper = cropperRef.current.cropper;
    const canvas = cropper.getCroppedCanvas();
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append(imageType === 'cover' ? 'cover_pic' : 'profile_pic', blob, 'cropped-image.jpg');

      try {
        await axios.patch(`${BASE_URL}/api/users/profile/update/`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        });

        const updatedImageURL = URL.createObjectURL(blob);
        setProfile((prevProfile) => ({
          ...prevProfile,
          [imageType === 'cover' ? 'cover_pic' : 'profile_pic']: updatedImageURL,
        }));

        // Dispatch the action to update the Redux store
        if (imageType === 'profile') {
          dispatch(updateUserProfileImage(updatedImageURL));
        }

        closeModal();

        // setProfile((prevProfile) => ({
        //   ...prevProfile,
        //   [imageType === 'cover' ? 'cover_pic' : 'profile_pic']: URL.createObjectURL(blob),
        // }));
        // closeModal();
      } catch (err) {
        setError('An error occurred while uploading the image.');
      }
    }, 'image/jpeg');
  };

  const handlePostSubmit = async () => {
    if (!newPost.trim()) {
      setError('Post content cannot be empty.');
      return;
    }

    try {
      setPosts((prevPosts) => [
        ...prevPosts,
        { id: Date.now(), content: newPost },
      ]);
      setNewPost('');
    } catch (err) {
      setError('An error occurred while submitting the post.');
    }
  };

  const openModal = (type) => {
    setImageType(type);
    document.getElementById(`${type}-upload`).click(); // Trigger the file input click
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalImage('');
  };

  if (error) {
    return <div className="text-red-500 text-center mt-4">{error}</div>;
  }

  if (!profile) {
    return <div className="text-center mt-4">Loading...</div>;
  }

  const handleNewPost = async (postData) => {
    // Handle post submission logic here
    // For example, send the post data to your backend API
    const userId = user.id;
    try {
      const formData = new FormData();
      formData.append('content', postData.content);
      formData.append('user', user.id);

      if (postData.image) {
        const imageBlob = await fetch(postData.image).then((res) => res.blob());
        formData.append('image', imageBlob, 'post.jpg');
      }
  
      
      if (postData.video) {
        const videoBlob = await fetch(postData.video).then((res) => res.blob());
        formData.append('video', videoBlob, 'post.mp4');
      }
      formData.append('is_approved', true);
      for (const [key, value] of formData.entries()) {
        console.log(`${key}:`, value);
      }
      // const response = await axios.post(`${BASE_URL}/api/posts/`, formData, {
      //   headers: {
      //     Authorization: `Bearer ${token}`,
      //     'Content-Type': 'multipart/form-data',
      //   },
      // });
      const response = await axios.get(`${BASE_URL}/api/posts/?user_id=${user.id}`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
      });
      // Update the posts state with the new post
      setPosts((prevPosts) => [response.data, ...prevPosts]);
      showSuccessToast("Post successfully submitted");
    } catch (err) {
       showErrorToast("Error while submitting!")
    }
  };


  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
  {/* Cover Photo */}
  <div
    className="relative h-48 sm:h-60 bg-cover bg-center cursor-pointer"
    style={{ backgroundImage: `url(${profile.cover_pic || 'https://via.placeholder.com/800x500'})` }}
  >
    <label
      htmlFor="cover-upload"
      className="absolute top-2 right-2 sm:top-4 sm:right-4 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
    >
      <FaCamera className="text-gray-600 text-lg" />
    </label>
    <input
      id="cover-upload"
      type="file"
      accept="image/jpeg,image/jpg,image/png"
      onChange={(e) => handleFileChange(e, 'cover')}
      className="hidden"
    />
    <button
      onClick={() => openModal('cover')}
      className="absolute top-2 right-12 sm:top-4 sm:right-12 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
    >
      Upload
    </button>

    {/* Profile Picture */}
    <div className="absolute bottom-0 left-2 sm:left-4 transform -translate-y-1/2">
      <div className="relative">
        <img
          src={profile.profile_pic || 'https://via.placeholder.com/100'}
          alt="Profile"
          accept="image/jpeg,image/jpg,image/png"
          className="w-20 h-20 sm:w-24 sm:h-24 rounded-full border-4 border-white shadow-md cursor-pointer object-cover"
          onClick={() => openModal('profile')}
        />
        <label
          htmlFor="profile-upload"
          className="absolute bottom-1 left-10 sm:left-12 cursor-pointer bg-white p-1 rounded-full shadow-lg hover:bg-gray-100"
        >
          <FaCamera className="text-gray-600 text-lg" />
        </label>
        <input
          id="profile-upload"
          type="file"
          onChange={(e) => handleFileChange(e, 'profile')}
          className="hidden"
        />
        <button
          onClick={() => openModal('profile')}
          className="absolute bottom-1 left-16 sm:left-24 bg-blue-500 text-white px-2 py-1 rounded-full shadow-lg hover:bg-blue-600"
        >
          Upload
        </button>
      </div>
    </div>
  </div>

  {/* Profile Details */}
  <div className="p-4 sm:p-6">
    {/* Profile Section */}
    <div className="flex flex-col sm:flex-row justify-between items-center mb-4">
      {/* User Info */}
      <div className="text-center sm:text-left">
        <h2 className="text-xl sm:text-2xl font-semibold">{profile.first_name}</h2>
        <h2 className="text-sm text-teal-800 opacity-75 shadow-sm">{`@${profile.username}`}</h2>
      </div>

      {/* Action Buttons */}
      <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-4 mt-4 sm:mt-0">
        {/* Profile Link */}
        <Link
          to="/profile-detail"
          className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white w-full sm:w-auto"
        >
          Profile
        </Link>
        {/* Edit Profile Link */}
        <Link
          to="/edit-profile"
          className="text-blue-500 border border-blue-500 px-4 py-2 rounded-full hover:bg-blue-500 hover:text-white w-full sm:w-auto"
        >
          Edit Profile
        </Link>
      </div>
    </div>

    {/* Bio Section */}
   <p className="text-gray-600 mb-6 text-center sm:text-left">
  {profile.bio || 'No bio available'}
</p>

    <PostBox onPostSubmit={handleNewPost} />

    {/* Posts List */}
    <div>
      {posts.map((post) => (
        <div key={post.id} className="p-4 border-b border-gray-200">
          <p>{post.content}</p>
          {post.image && <img src={post.image} alt="Post" className="mt-2 max-w-full h-auto" />}
          {post.video && <video src={post.video} controls className="mt-2 max-w-full h-auto" />}
        </div>
      ))}
    </div>
  </div>

  {/* Cropper Modal */}
  <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="w-full max-w-2xl mx-auto mt-16">
    <ImageCropper modalImage={modalImage} onSave={handleImageUpload} onClose={closeModal} ref={cropperRef} />
  </Modal>
</div>

  );
};

export default ProfilePage;
