import React, { useState, useRef } from 'react';
import EmojiPicker from 'emoji-picker-react';
import ImageCropper from '../components/Cropper';
import Modal from 'react-modal';
import { FaCamera, FaSmile } from 'react-icons/fa';

Modal.setAppElement('#root');

const ProfilePage = () => {
  const [newPost, setNewPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [modalImage, setModalImage] = useState('');
  const [imageType, setImageType] = useState(''); // 'cover' or 'profile'
  const cropperRef = useRef(null);

  const handlePostSubmit = async () => {
    if (!newPost.trim()) {
      alert('Post content cannot be empty.');
      return;
    }

    try {
      setPosts((prevPosts) => [
        ...prevPosts,
        { id: Date.now(), content: newPost },
      ]);
      setNewPost('');
    } catch (err) {
      alert('An error occurred while submitting the post.');
    }
  };

  const onEmojiClick = (event, emojiObject) => {
    setNewPost((prevPost) => prevPost + emojiObject.emoji);
    setShowEmojiPicker(false);
  };

  const openModal = (type) => {
    setImageType(type);
    document.getElementById(`${type}-upload`).click(); // Trigger the file input click
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setModalImage('');
  };

  return (
    <div className="max-w-3xl mx-auto bg-white shadow-md rounded-md overflow-hidden mt-8">
      {/* Post Box */}
      <div className="p-6 bg-gray-100 rounded-lg mb-6 shadow-lg">
        <div className="relative">
          <textarea
            className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none h-24"
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
          />
          <button
            onClick={() => setShowEmojiPicker((prev) => !prev)}
            className="absolute bottom-3 right-12 text-gray-500 hover:text-gray-700"
          >
            <FaSmile className="text-xl" />
          </button>
          {showEmojiPicker && (
            <div className="absolute bottom-16 right-0 z-50">
              <EmojiPicker onEmojiClick={onEmojiClick} />
            </div>
          )}
        </div>
        <button
          onClick={handlePostSubmit}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md shadow-lg hover:bg-blue-600"
        >
          Post
        </button>
      </div>

      {/* Posts List */}
      <div>
        {posts.map((post) => (
          <div key={post.id} className="p-4 border-b border-gray-200">
            <p>{post.content}</p>
          </div>
        ))}
      </div>

      {/* Cropper Modal */}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="w-full max-w-2xl mx-auto mt-16">
        <ImageCropper
          modalImage={modalImage}
          onSave={handleImageUpload}
          onClose={closeModal}
          ref={cropperRef}
        />
      </Modal>
    </div>
  );
};

export default ProfilePage;
