import React, { useState, useRef } from 'react';

const VideoTrimmer = ({ videoUrl, videoDuration, onSave, onClose }) => {
  const [startTime, setStartTime] = useState(0);
  const [endTime, setEndTime] = useState(videoDuration);
  const [trimmedVideo, setTrimmedVideo] = useState(null);
  const videoRef = useRef(null);
  const rangeRef = useRef(null);

  const handleTrim = () => {
    const trimmedVideoUrl = getTrimmedVideoUrl(videoUrl, startTime, endTime);
    setTrimmedVideo(trimmedVideoUrl);
  };

  const getTrimmedVideoUrl = (videoUrl, startTime, endTime) => {
    // Use a library like FFmpeg.js to trim the video
    // For simplicity, we'll just return a mock trimmed video URL
    return `https://example.com/trimmed-video.mp4`;
  };

  const handleRangeChange = (e) => {
    const rangeValue = e.target.value;
    const startTime = rangeValue / 100 * videoDuration;
    const endTime = startTime + 60; // Trim to 1 minute
    setStartTime(startTime);
    setEndTime(endTime);
  };

  return (
    <div>
      <video
        ref={videoRef}
        src={videoUrl}
        controls
        width="100%"
        height="100%"
      />
      <input
        type="range"
        ref={rangeRef}
        min="0"
        max="100"
        value={(startTime / videoDuration) * 100}
        onChange={handleRangeChange}
      />
      <button onClick={handleTrim}>Trim</button>
      {trimmedVideo && (
        <video
          src={trimmedVideo}
          controls
          width="100%"
          height="100%"
        />
      )}
      <button onClick={onClose}>Close</button>
      <button onClick={() => onSave(trimmedVideo)}>Save</button>
    </div>
  );
};

export default VideoTrimmer;
