import React, { useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import { FaBars, FaHome, FaUser, FaBell, FaBookmark, FaCog, FaSignOutAlt } from 'react-icons/fa';
import logo from '../assets/logo.png';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../features/auth/userSlice';
import SearchBar from '../components/SearchBar';

const Layout = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Sidebar hidden by default for mobile
  const user = useSelector((state) => state.user.user);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleLogout = () => {
    dispatch(logout());
    navigate('/login');
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside
        className={`bg-gray-900 text-white w-64 transform ${
          isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
        } md:translate-x-0 fixed inset-y-0 left-0 transition-transform duration-300 ease-in-out z-40 md:z-auto`}
      >
        {/* Sidebar header with profile */}
        <div className="p-6 flex flex-col items-center">
          <img
            src={user?.profile_pic || 'https://via.placeholder.com/100'}
            alt="Profile"
            className="w-16 h-16 rounded-full object-cover"
          />
          <h2 className="mt-4 text-lg font-semibold">{user?.username || 'John Doe'}</h2>
        </div>

        {/* Sidebar Links */}
        <nav className="mt-4 space-y-2">
          <a href="/home" className="flex items-center p-3 hover:bg-gray-700 rounded-md">
            <FaHome className="mr-3" /> Home
          </a>
          <a href="/profile" className="flex items-center p-3 hover:bg-gray-700 rounded-md">
            <FaUser className="mr-3" /> Profile
          </a>
          <a href="/bookmarks" className="flex items-center p-3 hover:bg-gray-700 rounded-md">
            <FaBookmark className="mr-3" /> Bookmarks
          </a>
          <a href="/settings" className="flex items-center p-3 hover:bg-gray-700 rounded-md">
            <FaCog className="mr-3" /> Settings
          </a>
          <button
            onClick={handleLogout}
            className="flex items-center p-3 hover:bg-gray-700 w-full rounded-md"
          >
            <FaSignOutAlt className="mr-3" /> Logout
          </button>
        </nav>
      </aside>

      {/* Main Content */}
      <div className="flex flex-col flex-grow">
        {/* Top Navigation Bar */}
        <header className="bg-white shadow-sm fixed inset-x-0 top-0 z-30 flex items-center justify-between px-6 py-4">
          {/* Left Side - Logo and Hamburger Menu */}
          <div className="flex items-center">
            <button onClick={toggleSidebar} className="text-gray-800 md:hidden mr-4">
              <FaBars />
            </button>
            <img src={logo} alt="Logo" className="w-10 h-10" />
            <h1 className="text-2xl font-bold ml-2">Connectify</h1>
          </div>

          {/* Search Bar */}
          <div className="w-1/2 max-w-md hidden sm:block">
            <SearchBar />
          </div>

          {/* Right Side - Icons */}
          <div className="flex items-center space-x-6">
            <button className="text-gray-800">
              <FaBell size={24} />
            </button>
            <button className="text-gray-800">
              <FaUser size={24} />
            </button>
            <img
              src={user?.profile_pic || 'https://via.placeholder.com/30'}
              alt="Profile"
              className="w-8 h-8 rounded-full object-cover"
            />
          </div>
        </header>

        {/* Main Feed Area */}
        <main className="mt-16 flex-grow p-6 bg-gray-100 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
