// src/components/SignupForm.jsx
import React, { useState } from 'react';
import logo from '../assets/logo.png';
import axios from 'axios';
import { BASE_URL } from '../config';
import { showSuccessToast } from './CustomToast';
import { showErrorToast } from './ErroToast';
const SignupForm = () => {
  const [formData, setFormData] = useState({
    username: '',
    first_name: '',
    email: '',
    dob: '',
    mobile: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      profile_pic: e.target.files[0],
    });
  };

  const handleSubmit = async(e) => {
    e.preventDefault();

    // Creating form data for backend API
    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      const response = await axios.post(`${BASE_URL}/api/users/register/`,data,{
        headers:{
          'Content-Type': 'multipart/form-data' 
        }
        
      });
      const { token, user } = response.data; 
      localStorage.setItem('token', token);
      dispatch(loginSuccess({ user, token }));
      dispatch(setUser(user));
      showSuccessToast('Registration successful!');
      setTimeout(() => {
        navigate('/');
      }, 2000);
    } catch (error) {
      console.error('Error signing up:', error.response?.data || error.message);
      showErrorToast('Error signing up. Please try again.')
    }

    
  };

  return (
    <form
    onSubmit={handleSubmit}
    className="w-full max-w-md bg-white p-8 shadow-md rounded-xl"
  >
     <div className="flex flex-col items-center mb-2">
      {/* Logo Image */}
      <img
        src={logo} // Replace with your image path or URL
        alt="Logo"
        className="w-22 h-20 mb-1"
      />
      {/* App Name */}
      <h1 className="text-3xl font-semibold text-gray-800">Connectify</h1>
    </div>
    <h2 className="text-2xl font-semibold text-center text-gray-700 mb-6">Create an Account</h2>

    <div className="space-y-4">
      <input
        type="text"
        name="username"
        placeholder="Username"
        value={formData.username}
        onChange={handleChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:ring-2 focus:ring-blue-400"
        required
      />
      <input
        type="text"
        name="first_name"
        placeholder="First Name"
        value={formData.first_name}
        onChange={handleChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:ring-2 focus:ring-blue-400"
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:ring-2 focus:ring-blue-400"
        required
      />
      {/* <textarea
        name="bio"
        placeholder="Bio"
        value={formData.bio}
        onChange={handleChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:ring-2 focus:ring-blue-400 resize-none"
        rows="3"
      /> */}
      <input
        type="date"
        name="dob"
        placeholder="Date of Birth"
        value={formData.dob}
        onChange={handleChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:ring-2 focus:ring-blue-400"
      />
      <input
        type="tel"
        name="mobile"
        placeholder="Mobile Number"
        value={formData.mobile}
        onChange={handleChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:ring-2 focus:ring-blue-400"
      />
      {/* <input
        type="file"
        name="profile_pic"
        accept="image/*"
        onChange={handleFileChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:outline-none"
      /> */}
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={formData.password}
        onChange={handleChange}
        className="w-full p-3 border border-gray-200 rounded-md focus:ring-2 focus:ring-blue-400"
        required
      />
    </div>

    <button
      type="submit"
      className="w-full mt-6 bg-blue-500 text-white py-3 rounded-md hover:bg-blue-600 transition-colors"
    >
      Sign Up
    </button>
  </form>
  );
};

export default SignupForm;
