import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'; 
import axios from 'axios';
import { BASE_URL } from '../../config';

// Updated to accept page parameter
export const fetchFeed = createAsyncThunk(
  'feed/fetchFeed',
  async ({ token, page }, { rejectWithValue }) => {
    try {
      const response = await axios.get(`${BASE_URL}/api/interactions/feed/?page=${page}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      return { posts: response.data, page };
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const feedSlice = createSlice({
  name: 'feed',
  initialState: {
    posts: [],
    loading: false,
    error: null,
    hasMore: true,
    likedPosts: {}, // Add likedPosts state
    bookmarkPosts: {}, // Add bookmarkPosts state
  },
  reducers: {
    updatePostLikes: (state, action) => {
      const postIndex = state.posts.findIndex(post => post.id === action.payload.postId);
      if (postIndex !== -1) {
        state.posts[postIndex].likes = action.payload.likes;
        state.posts[postIndex].total_likes = action.payload.totalLikes;
        state.likedPosts[action.payload.postId] = action.payload.liked; // Update likedPosts state
      }
    },
    updatePostBookmarks: (state, action) => {
      const postIndex = state.posts.findIndex(post => post.id === action.payload.postId);
      if (postIndex !== -1) {
        state.posts[postIndex].bookmarked = action.payload.bookmarked;
        state.bookmarkPosts[action.payload.postId] = action.payload.bookmarked; // Update bookmarkPosts state
      }
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchFeed.fulfilled, (state, action) => {
        const { posts, page } = action.payload;
        state.loading = false;
        state.error = null;

        if (page === 1) {
          // Replace posts if it's the first page
          state.posts = posts;
        } else {
          // Append posts for subsequent pages
          state.posts = [...state.posts, ...posts];
        }

        // Determine if there are more posts to load
        state.hasMore = posts.length > 0;
      })
      .addCase(fetchFeed.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload.message;
      });
  },
});


export const { updatePostLikes, updatePostComments } = feedSlice.actions;

export default feedSlice.reducer;
