import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';

import { BASE_URL } from '../config'; // Assuming your BASE_URL is in a config file
import UserList from '../components/UserList';

const FollowersFollowingPage = () => {
  const [followers, setFollowers] = useState([]);
  const [following, setFollowing] = useState([]);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('followers'); // Track active tab
  const { user, token } = useSelector((state) => state.auth); // Get user and token from Redux store

  const userId = user.id; // Assume the profile owner is the logged-in user

  // Fetch Followers and Following Data
  const fetchFollowersAndFollowing = async () => {
    try {
      const followersRes = await axios.get(`${BASE_URL}/api/interactions/${userId}/followers/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFollowers(followersRes.data);

      const followingRes = await axios.get(`${BASE_URL}/api/interactions/${userId}/following/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFollowing(followingRes.data);
    } catch (err) {
      setError(err.response?.data?.detail || 'Error fetching followers/following data.');
    }
  };

  useEffect(() => {
    if (userId) {
      fetchFollowersAndFollowing();
    }
  }, [userId]);

  // Action handlers for unfollowing or removing a user
  const handleUnfollow = async (id) => {
    try {
      await axios.post(`${BASE_URL}/api/interactions/unfollow/${id}/`, { follower_id: user.id }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFollowing(following.filter((user) => user.id !== id));
    } catch (err) {
      setError('Error unfollowing user.');
    }
  };

  const handleRemoveFollower = async (id) => {
    try {
      await axios.post(`${BASE_URL}/api/interactions/remove_follower/${id}/`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFollowers(followers.filter((user) => user.id !== id));
    } catch (err) {
      setError('Error removing follower.');
    }
  };

  return (
    <div className="container mx-auto p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Followers and Following</h1>
        <div className="flex space-x-4">
          <button
            className={`py-2 px-4 ${activeTab === 'followers' ? 'bg-blue-500 text-white' : 'bg-gray-100 text-gray-600'}`}
            onClick={() => setActiveTab('followers')}
          >
            Followers ({followers.length})
          </button>
          <button
            className={`py-2 px-4 ${activeTab === 'following' ? 'bg-blue-500 text-white' : 'bg-gray-100 text-gray-600'}`}
            onClick={() => setActiveTab('following')}
          >
            Following ({following.length})
          </button>
        </div>
      </div>

      {/* Conditional Rendering of Followers or Following */}
      {activeTab === 'followers' ? (
        <UserList users={followers} type="followers" onAction={handleRemoveFollower} />
      ) : (
        <UserList users={following} type="following" onAction={handleUnfollow} />
      )}

      {error && <p className="text-red-500 mt-4">{error}</p>}
    </div>
  );
};

export default FollowersFollowingPage;
