// CroppedImageModal.js
import React from 'react';
import { FaTimes } from 'react-icons/fa';

const CroppedImageModal = ({ croppedImage, onClose, onRemove }) => {
  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white p-4 rounded-md shadow-lg">
        <img src={croppedImage} alt="Cropped" className="max-w-full h-auto rounded-md mb-2" />
        <div className="flex justify-between">
          <button
            className="bg-red-500 text-white p-1 rounded-full"
            onClick={onRemove}
          >
            <FaTimes />
          </button>
          <button
            className="bg-gray-500 text-white p-1 rounded-full"
            onClick={onClose}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default CroppedImageModal;
