import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchRoom } from '../features/auth/groupChatSlice';
import { fetchMessages, sendMessage } from '../features/auth/chatSlice';

const GroupChatBox = () => {
  const { roomId } = useParams(); 
  const token = useSelector((state)=>state.auth.token)  
  const dispatch = useDispatch();
  const { group, loading, error } = useSelector((state) => state.groupChat);
  const [messageInput, setMessageInput] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [file, setFile] = useState(null);
  const messagesEndRef = useRef(null);
  const messagesContainerRef = useRef(null);

  useEffect(() => {
    dispatch(fetchRoom({ roomId,token }));
  }, [dispatch, token]);

  useEffect(() => {
    if (roomId) {
        if(group){
      dispatch(fetchMessages({ roomName:group_name, token }));
    }
    }
  }, [dispatch, roomId, token]);

  // Scroll to bottom when new messages are added
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Load older messages when scrolling to the top
  const handleScroll = (e) => {
    if (e.target.scrollTop === 0) {
      //dispatch(loadOlderMessages({ roomId, token }));
    }
  };

  const handleSendMessage = async () => {
    if (messageInput.trim() || file) {
      await dispatch(sendMessage({ roomId, message: messageInput, file, token }));
      setMessageInput('');
      setFile(null);
    }
  };

  const handleAddEmoji = (emoji) => {
    setMessageInput((prev) => prev + emoji.native);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }
  return (



<div className="group-chat-box">
      <div className="chat-header">
      {group && group.group_name ? (
        <h2 className="text-2xl font-bold">Chat Room: {group.group_name}</h2>
      ):(<h2 className="text-2xl font-bold">Chat Room Details Not Available</h2>)}</div>
      <div
        className="messages-container"
        ref={messagesContainerRef}
        onScroll={handleScroll}
        style={{ overflowY: 'auto', height: '70vh' }}
      >
        {messages.map((msg) => (
          <div key={msg.id} className="message">
            <div className="message-sender">{msg.sender}</div>
            <div className="message-text">{msg.message}</div>
            {/* Add file display logic if msg has a file */}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="input-container">
        <div className="emoji-picker-container">
          {showEmojiPicker && (
            <Picker onEmojiSelect={handleAddEmoji} />
          )}
        </div>
        <input
          type="text"
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
          placeholder="Type your message..."
          className="message-input"
        />
        <button onClick={() => setShowEmojiPicker((prev) => !prev)}>
          <FaSmile />
        </button>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleSendMessage} className="send-button">
          Send
        </button>
      </div>
    </div>
  );
};

export default GroupChatBox;
