import React, { useEffect, useState } from 'react';
import { Outlet, useLocation, useNavigate } from 'react-router-dom';
import { FaBars, FaTimes, FaHome, FaUser, FaBookmark, FaCog, FaSignOutAlt, FaBell } from 'react-icons/fa';
import logo from '../assets/logo.png';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../features/auth/userSlice';
import SearchBar from '../components/SearchBar';
import Suggestion from '../components/SuggestionComponent';
import { fetchSuggestions } from '../features/auth/suggestionSlice';
import { RingLoader, FadeLoader } from 'react-spinners';
import useNotifications from '../components/NotificationService';
const Layout = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [isNotificationsOpen, setIsNotificationsOpen] = useState(false);
  const user = useSelector((state) => state.user.user);
  const token = useSelector((state) => state.auth.token);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  const { suggestions: userSuggestions, loading: isLoading, error: fetchError } = useSelector((state) => state.suggestions);
  useEffect(() => {
    if (user && token) {
      dispatch(fetchSuggestions(token));
    }
  }, [user, token, dispatch]);
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };
  useNotifications((notification) => {
    setNotifications((prevNotifications) => [notification, ...prevNotifications]);
  });
  const toggleNotifications = () => {
    setIsNotificationsOpen(!isNotificationsOpen);
  };
  const handleLogout = () => {
    dispatch(logout());
    navigate('/login');
  };
  
  
 
  return (
    <div className="flex h-screen">
    {/* Sidebar */}
    <aside
      className={`bg-white text-gray-800 w-64 transform ${
        isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
      } lg:translate-x-0 fixed inset-y-0 left-0 transition-transform duration-300 ease-in-out z-50 shadow-lg`}
    >
      {/* Sidebar header with logo and profile */}
      <div className="p-6 border-b flex items-center justify-between">
        {/* Logo Section */}
        <div className="flex items-center">
          <img src={logo} alt="Logo" className="w-10 h-10" />
          <h1 className="text-2xl font-bold ml-2">Connectify</h1>
        </div>

        {/* Close button (visible only on small and medium screens) */}
        <button className="lg:hidden text-gray-800" onClick={toggleSidebar}>
          <FaTimes size={20} />
        </button>
      </div>

      {/* Profile Section */}
      <div className="flex flex-col border-b items-center p-6">
        <img
          src={user?.profile_pic || 'https://via.placeholder.com/100'}
          alt="Profile"
          className="w-16 h-16 rounded-full object-cover"
        />
        <h2 className="mt-4 text-lg font-semibold">{user?.username || 'John Doe'}</h2>
      </div>

      {/* Sidebar Links */}
      <nav className="mt-4 space-y-2">
        <a
          href="/home"
          className="flex items-center p-3 hover:bg-gray-200 rounded-md"
          onClick={() => setIsSidebarOpen(false)}
        >
          <FaHome className="mr-3" /> Home
        </a>
        <a
          href="/profile"
          className="flex items-center p-3 hover:bg-gray-200 rounded-md"
          onClick={() => setIsSidebarOpen(false)}
        >
          <FaUser className="mr-3" /> Profile
        </a>
        <a
          href="/bookmarks"
          className="flex items-center p-3 hover:bg-gray-200 rounded-md"
          onClick={() => setIsSidebarOpen(false)}
        >
          <FaBookmark className="mr-3" /> Bookmarks
        </a>
        <a
          href="/settings"
          className="flex items-center p-3 hover:bg-gray-200 rounded-md"
          onClick={() => setIsSidebarOpen(false)}
        >
          <FaCog className="mr-3" /> Settings
        </a>
        <button
          onClick={() => {
            handleLogout();
            setIsSidebarOpen(false);
          }}
          className="flex items-center p-3 hover:bg-gray-200 w-full rounded-md"
        >
          <FaSignOutAlt className="mr-3" /> Logout
        </button>
      </nav>
    </aside>

    {/* Main Content */}
    <div
      className={`flex flex-col flex-grow transition-all duration-300 ${
        isSidebarOpen ? 'ml-64' : 'ml-0'
      } lg:ml-64`} // Adjust margin based on sidebar state
    >
      {/* Top Navigation Bar */}
      <header className="bg-white shadow-md fixed inset-x-0 top-0 z-30 flex items-center justify-between px-6 py-4">
        {/* Left Side - Hamburger Menu (Hidden on larger screens) */}
        <div className="flex items-center">
          <button onClick={toggleSidebar} className="text-gray-800 lg:hidden mr-4">
            <FaBars />
          </button>
        </div>

        {/* Search Bar */}
        <div className="w-full max-w-md">
          <SearchBar />
        </div>

        {/* Right Side - Notifications and User Profile */}
           {/* Right Side - Notifications and User Profile */}
           <div className="flex items-center space-x-6 relative">
            <button onClick={toggleNotifications} className="text-gray-800">
              <FaBell size={24} />
              {notifications.length > 0 && (
                <span className="absolute top-0 right-0 bg-red-600 text-white text-xs rounded-full w-4 h-4 flex items-center justify-center">
                  {notifications.length}
                </span>
              )}
            </button>
            {isNotificationsOpen && (
              <div className="absolute right-0 mt-2 w-64 bg-white shadow-lg rounded-md overflow-hidden">
                <ul>
                  {notifications.length === 0 ? (
                    <li className="p-4 text-gray-600">No new notifications</li>
                  ) : (
                    notifications.map((notification, index) => (
                      <li key={index} className="p-4 border-b text-gray-800">
                        {notification}
                      </li>
                    ))
                  )}
                </ul>
              </div>
            )}
            <img
              src={user?.profile_pic || 'https://via.placeholder.com/30'}
              alt="Profile"
              className="w-8 h-8 rounded-full object-cover"
            />
          </div>
      </header>

      {/* Main Feed Area */}
      <main className="mt-16  flex-grow p-6 bg-gray-100 overflow-auto flex flex-col md:flex-row">
        <div className="flex-1 flex flex-col md:flex-row">
          <div className="flex-1 max-w-3xl pr-2 md:w-2/3"> {/* Main Content */}
            <Outlet />
          </div>
          {location.pathname === '/home' || location.pathname === '/profile'  && (
              <div className="bg-white shadow-md rounded-md overflow-hidden mt-8 md:mt-8 md:w-1/3 w-full sm:max-w-sm mx-auto p-2">
                {isLoading ? (
                  <div className="flex items-center justify-center p-6">
                    <RingLoader color="#4A90E2" size={60} />
                    <p className="ml-4">Loading suggestions...</p>
                  </div>
                ) : fetchError ? (
                  <div className="flex items-center justify-center p-6">
                    <FadeLoader color="#E74C3C" height={15} width={5} />
                    <p className="ml-4 text-red-500">Error fetching suggestions: {fetchError}</p>
                  </div>
                ) : (
                  <Suggestion suggestions={userSuggestions} />
                )}
              </div>
            )}
        </div>
      </main>
    </div>
  </div>
  );
};

export default Layout;
