// src/layouts/Layout.jsx
import React, { useState } from 'react';
import { Outlet } from 'react-router-dom'; // For dynamically loading components in the center
import { FaBars, FaTimes } from 'react-icons/fa'; // Icons for the collapse functionality

const Layout = () => {
  const [isNavCollapsed, setIsNavCollapsed] = useState(false);

  const toggleNavbar = () => {
    setIsNavCollapsed(!isNavCollapsed);
  };

  return (
    <div className="flex h-screen overflow-hidden">
    {/* Collapsible Sidebar */}
    <aside
      className={`bg-gray-800 text-white fixed md:relative inset-y-0 left-0 transform ${
        isNavCollapsed ? '-translate-x-full' : 'translate-x-0'
      } md:translate-x-0 transition-transform duration-300 md:w-64 w-64 z-40`}
    >
      <div className="flex justify-between items-center p-4 md:hidden">
        <button onClick={toggleNavbar} className="text-xl">
          {isNavCollapsed ? <FaBars /> : <FaTimes />}
        </button>
      </div>
      <nav className="flex flex-col space-y-2 mt-4 px-2">
        <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
          Dashboard
        </a>
        <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
          Profile
        </a>
        <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
          Settings
        </a>
        <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
          Logout
        </a>
      </nav>
    </aside>

    {/* Main Content Area */}
    <div className="flex flex-col flex-grow md:ml-64 transition-all duration-300">
      {/* Header */}
      <header className="bg-white shadow-md py-4 px-6 flex items-center justify-between">
        {/* Hamburger Menu Button */}
        <button
          onClick={toggleNavbar}
          className="text-xl md:hidden"
        >
          {isNavCollapsed ? <FaBars /> : <FaTimes />}
        </button>
        <h1 className="text-2xl font-semibold text-gray-800">My Application</h1>
      </header>

      {/* Content Area */}
      <main className="flex-grow p-6 bg-gray-100 overflow-auto">
        <Outlet /> {/* This will render the routed components */}
      </main>
    </div>
  </div>
  );
};

export default Layout;
