import React, { useState } from 'react';
import ChatDialog from '../components/ChatDialog';
import UserList from '../components/UserListMessage';

const ChatDialogMessage = () => {
  const [selectedUser, setSelectedUser] = useState(null);
  const [roomName, setRoomName] = useState(null);
  const handleUserSelect = (user,generatedRoomName) => {
    setSelectedUser(user); // Set the selected user for chat
    setRoomName(generatedRoomName);
  };

  return (
    <div className="flex h-screen">
    {/* UserList Section */}
    <div className="w-1/3 md:w-1/4 border-r p-4 overflow-y-auto bg-white" style={{ height: '100vh', position: 'fixed' }}>
      <UserList onUserSelect={handleUserSelect} />
    </div>

    {/* ChatDialog Section */}
    <div className="w-2/3 md:w-3/4 flex-grow p-4 bg-gray-100" style={{ marginLeft: '15%',height: '100vh', position: 'fixed' }}> 
      {selectedUser ? (
        <ChatDialog roomName={roomName} user={selectedUser} />
      ) : (
        <div className="text-center text-gray-500">Select a user to start chatting</div>
      )}
    </div>
  </div>
  );
};

export default ChatDialogMessage;
