import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import { BASE_URL } from '../../config';
import { fetchPosts } from './postSlice';


export const fetchLikeStatus = createAsyncThunk(
  'likedPosts/fetchLikeStatus',
  async ({ posts, token }) => {
    const likeStatuses = {};
    for (let post of posts) {
      const response = await fetch(`${BASE_URL}/api/posts/${post.id}/check_liked/`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const result = await response.json();
        likeStatuses[post.id] = result.liked; // Assume the response contains { liked: true/false }
      }
    }
    return likeStatuses;
  }
);

export const handleLike = createAsyncThunk(
  'likedPosts/handleLike',
  async ({ postId, token ,userId}, { dispatch }) => {
    const response = await fetch(`${BASE_URL}/api/likes/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ post: postId }),
    });
    dispatch(fetchPosts({ userId, token }))
    if (response.status === 201) {
      return { postId, liked: true };
    } else if (response.status === 204) {
      return { postId, liked: false };
    } else {
      throw new Error('Failed to like/unlike post');
    }
    
  }
);

const likedPostsSlice = createSlice({
  name: 'likedPosts',
  initialState: {},
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchLikeStatus.fulfilled, (state, action) => {
        return { ...state, ...action.payload };
      })
      .addCase(handleLike.fulfilled, (state, action) => {
        state[action.payload.postId] = action.payload.liked;
      })
      .addCase(handleLike.rejected, (state, action) => {
        state.error = action.payload.message; // Extract the error message
      });
  },
});

export default likedPostsSlice.reducer;
