// src/layouts/Layout.jsx
import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import { FaBars } from 'react-icons/fa';
import logo from '../assets/logo.png';
const Layout = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside
        className={`bg-gray-800 text-white ${
          isSidebarOpen ? 'block' : 'hidden'
        } fixed inset-y-0 left-0 transform md:translate-x-0 z-30 w-64 md:block transition-all duration-300 ease-in-out`}
      >
        {/* Hamburger Button for Mobile */}
        <div className="flex justify-end p-4 md:hidden">
          {/* Visible only on smaller screens */}
          <button onClick={toggleSidebar} className="text-white">
            <FaBars />
          </button>
        </div>

        {/* Profile Section */}
        <div className="flex items-center p-4">
          <img
            src="https://via.placeholder.com/30" // Dummy rounded profile image
            alt="Profile"
            className="rounded-full mr-2"
          />
          <span>John Doe</span> {/* Dummy profile name */}
        </div>

        {/* Sidebar Links */}
        <nav className="mt-4 px-2 space-y-2">
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
            Profile
          </a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
            Dashboard
          </a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
            Settings
          </a>
          <a href="#" className="block py-2 px-3 hover:bg-gray-700 rounded">
            Logout
          </a>
        </nav>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col ml-0 md:ml-64 transition-all duration-300 ease-in-out">
        {/* Header */}
        <header className="bg-white shadow-md flex items-center justify-between px-6 py-4 md:ml-0">

        <div className="flex items-center mb-4">
        <img src={logo} alt="Logo" className="w-8 h-8 mr-0" />
          <h1 className="text-1xl font-bold">Connectify</h1>
        </div>
          {/* Hamburger Menu Button */}
          <div className="md:hidden">
            <button onClick={toggleSidebar} className="text-gray-800">
              <FaBars />
            </button>
          </div>
        </header>

        {/* Content Area */}
        <main className="flex-grow p-6 bg-gray-100 overflow-auto">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Layout;
